ALTER TABLE `PROJECT_MASTER`
    ADD `SECMAIL_YN` char(1) DEFAULT 'Y',
    ADD `SECMAIL_DESC` longtext DEFAULT NULL;
  
UPDATE T2_CODE_DTL SET CD_DTL_EXP='SELECT RTN.OSS_ID, RTN.OSS_NAME, RTN.OSS_VERSION, RTN.CVSS_SCORE, RTN.CVE_ID, RTN.VULN_SUMMARY, RTN.MODI_DATE, RTN.PUBL_DATE FROM ( SELECT T2.OSS_ID, T2.OSS_NAME, T2.OSS_VERSION , CONCAT(IF(HIS.CVSS_SCORE = '' OR HIS.CVSS_SCORE IS NULL, '0.0', HIS.CVSS_SCORE), ' -> ', T4.CVSS_SCORE) AS CVSS_SCORE , CONCAT(IF(HIS.CVE_ID = '' OR HIS.CVE_ID IS NULL, 'NONE', HIS.CVE_ID), ' -> ', T4.CVE_ID) AS CVE_ID , T5.VULN_SUMMARY, DATE_FORMAT(T5.MODI_DATE, '%Y-%m-%d') AS MODI_DATE, DATE_FORMAT(T5.PUBL_DATE, '%Y-%m-%d') AS PUBL_DATE FROM PROJECT_MASTER T1 INNER JOIN OSS_COMPONENTS T2 ON T1.PRJ_ID = T2.REFERENCE_ID AND T2.REFERENCE_DIV = '50' AND T2.EXCLUDE_YN <> 'Y' INNER JOIN OSS_MASTER T3 ON IFNULL(T2.REF_OSS_NAME, T2.OSS_NAME) = T3.OSS_NAME AND IFNULL(T2.OSS_VERSION, '') = IFNULL(T3.OSS_VERSION, '') AND T3.USE_YN = 'Y' INNER JOIN OSS_DISCOVERED_SND_EMAIL T4 ON T3.OSS_ID = T4.OSS_ID AND T4.SND_YN != 'Y'  LEFT JOIN NVD_CVE_V3 T5 ON T4.CVE_ID = T5.CVE_ID LEFT JOIN NVD_OSS_HIS HIS ON T4.OSS_ID = HIS.OSS_ID AND HIS.REG_DT > ADDDATE(SYSDATE(), INTERVAL - 1 DAY) WHERE T1.PRJ_ID = ?) RTN GROUP BY RTN.OSS_ID, RTN.CVE_ID ORDER BY RTN.OSS_NAME, RTN.CVSS_SCORE DESC, RTN.MODI_DATE DESC' WHERE CD_NO=104 AND CD_DTL_NO=207;
UPDATE T2_CODE_DTL SET CD_DTL_EXP='SELECT T0.OSS_ID, T1.OSS_NAME, T1.OSS_VERSION , CONCAT(IF(HIS.CVE_ID = '' OR HIS.CVE_ID IS NULL, 'NONE', HIS.CVE_ID), ' -> ', T2.CVE_ID) AS CVE_ID , CONCAT(IF(HIS.CVSS_SCORE = '' OR HIS.CVSS_SCORE IS NULL, '0.0', HIS.CVSS_SCORE), ' -> ', T2.CVSS_SCORE) AS CVSS_SCORE , T2.VULN_SUMMARY, DATE_FORMAT(T2.MODI_DATE, '%Y-%m-%d') AS MODI_DATE, DATE_FORMAT(T2.PUBL_DATE, '%Y-%m-%d') AS PUBL_DATE FROM OSS_DISCOVERED_SND_EMAIL T0 INNER JOIN OSS_MASTER T1 ON T0.OSS_ID = T1.OSS_ID AND T0.SND_YN = 'N' AND T1.USE_YN = 'Y' LEFT JOIN NVD_CVE_V3 T2 ON T0.CVE_ID = T2.CVE_ID LEFT JOIN NVD_OSS_HIS HIS ON T0.OSS_ID = HIS.OSS_ID AND HIS.REG_DT > ADDDATE(SYSDATE(), INTERVAL - 1 DAY)  WHERE ? GROUP BY T0.OSS_ID, T2.CVE_ID ORDER BY T1.OSS_NAME, T1.OSS_VERSION, T2.CVSS_SCORE DESC' WHERE CD_NO=104 AND CD_DTL_NO=208;