mapper.select:
SELECT OM.OSS_NAME , OM.OSS_VERSION , OM.LICENSE_DIV , TRIM(REPLACE(REPLACE(GROUP_CONCAT( CONCAT(OL.OSS_LICENSE_COMB, ' ', IF(LM.SHORT_IDENTIFIER = '' OR LM.SHORT_IDENTIFIER IS NULL, LM.LICENSE_NAME, LM.SHORT_IDENTIFIER)) ORDER BY OL.OSS_LICENSE_IDX SEPARATOR ' '), ' AND ', ','), ' OR ', '|')) AS DECLARED_LICENSE , (SELECT IFNULL(GROUP_CONCAT(IF(LM.SHORT_IDENTIFIER = '' OR LM.SHORT_IDENTIFIER IS NULL , LM.LICENSE_NAME , LM.SHORT_IDENTIFIER) ORDER BY OSS_LICENSE_IDX ASC) , '') FROM OSS_LICENSE_DETECTED ODT INNER JOIN LICENSE_MASTER LM ON ODT.LICENSE_ID = LM.LICENSE_ID WHERE ODT.OSS_ID = OM.OSS_ID) AS DETECTED_LICENSE , OM.COPYRIGHT , IF(OM.OBLIGATION_TYPE = '10' OR OM.OBLIGATION_TYPE = '11', 'Y', 'N') AS NOTICE , IF(OM.OBLIGATION_TYPE = '11', 'Y', 'N') AS SOURCE , OM.DEACTIVATE_FLAG AS DEACTIVATE FROM OSS_MASTER OM INNER JOIN OSS_LICENSE_DECLARED OL ON OM.OSS_ID = OL.OSS_ID INNER JOIN LICENSE_MASTER LM ON LM.LICENSE_ID = OL.LICENSE_ID WHERE OM.OSS_NAME = #{ossName}
AND OM.OSS_VERSION = #{ossVersion}
GROUP BY OM.OSS_ID
