mapper.select:
SELECT COUNT(*) FROM ( SELECT T1.PARTNER_ID , T1.PARTNER_NAME , T1.SOFTWARE_NAME , CASE WHEN T1.STATUS = 'PROG' THEN 'Progress' WHEN T1.STATUS = 'REQ' THEN 'Request' WHEN T1.STATUS = 'REV' THEN 'Review' WHEN T1.STATUS = 'CONF' THEN 'Confirm' END AS STATUS , T1.MODIFIED_DATE , T1.CREATED_DATE , T1.CREATOR FROM PARTNER_MASTER T1 WHERE T1.USE_YN = 'Y'
AND ( T1.CREATOR = #{userId} OR EXISTS ( SELECT 1 FROM PARTNER_WATCHER A1 WHERE A1.PARTNER_ID = T1.PARTNER_ID AND (A1.USER_ID = #{userId} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{userId}) )) )
OR PUBLIC_YN = 'Y'
)
) RESULT WHERE 1=1
RESULT.PARTNER_ID = #{item}
