/* ProjectMapper.selectProjectTotalCount */ SELECT COUNT(*) FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' ELSE 'PROG' END STATUS , T1.PRIORITY , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T1.DISTRIBUTION_TYPE , T3.CD_DTL_NM , T3.CD_DTL_NO FROM PROJECT_MASTER T1 LEFT JOIN T2_USERS T2 ON T1.CREATOR = T2.USER_ID LEFT JOIN (SELECT * FROM T2_CODE_DTL WHERE CD_NO = '200') T3 ON T1.DIVISION = T3.CD_DTL_NO WHERE T1.USE_YN = 'Y'
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= DATE_FORMAT(#{createDateFrom},'%Y%m%d')
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= DATE_FORMAT(#{createDateTo},'%Y%m%d')
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') >= DATE_FORMAT(#{updateDateFrom},'%Y%m%d')
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') <= DATE_FORMAT(#{updateDateTo},'%Y%m%d')
AND ( T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%')) OR T1.CREATOR LIKE CONCAT('%',#{creator},'%') )
AND EXISTS (SELECT 1 FROM PROJECT_MODEL M WHERE T1.PRJ_ID = M.PRJ_ID AND MODEL_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{modelName}, '_', '\\\\_'),'%'))
) A WHERE 1=1
AND A.STATUS = #{status}
AND ( A.CD_DTL_NM = #{division} OR A.CD_DTL_NO = #{division} )
A.PRJ_ID = #{item}

/* ProjectMapper.selectProject */ SELECT A.PRJ_ID , A.PRJ_NAME , A.PRJ_VERSION , DATE_FORMAT(A.MODIFIED_DATE,'%Y-%m-%d') AS UPDATE_DATE , DATE_FORMAT(A.CREATED_DATE,'%Y-%m-%d') AS CREATE_DATE , A.IDENTIFICATION_STATUS , A.VERIFICATION_STATUS , A.DESTRIBUTION_STATUS AS DISTRIBUTION_STATUS , A.DISTRIBUTION_TYPE , A.NETWORK_SERVER_TYPE AS NETWORK_SERVICE , A.NOTICE_TYPE AS NOTICE , A.NOTICE_TYPE_ETC AS NOTICE_PLATFORM , A.PRIORITY , A.STATUS , A.SRC_CSV_FILE_ID , A.BIN_CSV_FILE_ID , A.BIN_BINARY_FILE_ID FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.CREATED_DATE , T1.MODIFIED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' ELSE 'PROG' END STATUS , T1.PRIORITY , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T1.DISTRIBUTION_TYPE , T1.SRC_CSV_FILE_ID , T1.BIN_CSV_FILE_ID , T1.BIN_BINARY_FILE_ID , T3.CD_DTL_NM , T3.CD_DTL_NO , IF(T1.NETWORK_SERVER_TYPE IS NULL OR T1.NETWORK_SERVER_TYPE = '', 'NO', 'YES') AS NETWORK_SERVER_TYPE , T4.NOTICE_TYPE , T4.NOTICE_TYPE_ETC FROM PROJECT_MASTER T1 LEFT JOIN T2_USERS T2 ON T1.CREATOR = T2.USER_ID LEFT JOIN (SELECT * FROM T2_CODE_DTL WHERE CD_NO = '200') T3 ON T1.DIVISION = T3.CD_DTL_NO LEFT JOIN OSS_NOTICE T4 ON T1.PRJ_ID = T4.PRJ_ID WHERE T1.USE_YN = 'Y'
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= DATE_FORMAT(#{createDateFrom},'%Y%m%d')
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= DATE_FORMAT(#{createDateTo},'%Y%m%d')
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') >= DATE_FORMAT(#{updateDateFrom},'%Y%m%d')
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') <= DATE_FORMAT(#{updateDateTo},'%Y%m%d')
AND ( T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%')) OR T1.CREATOR LIKE CONCAT('%',#{creator},'%') )
AND EXISTS (SELECT 1 FROM PROJECT_MODEL M WHERE T1.PRJ_ID = M.PRJ_ID AND MODEL_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{modelName}, '_', '\\\\_'),'%'))
) A WHERE 1=1
AND A.STATUS = #{status}
AND ( A.CD_DTL_NM = #{division} OR A.CD_DTL_NO = #{division} )
A.PRJ_ID = #{item}
ORDER BY A.PRJ_ID DESC LIMIT 0, 1000

/* ProjectMapper.selectProjectCount */ SELECT COUNT(*) FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.DISTRIBUTION_TYPE , T1.OSS_NOTICE_DUE_DATE , T1.COMMENT , T1.OS_TYPE , T1.OS_TYPE_ETC , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' /* 2019-10-24 */ WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' /* 2019-10-24 */ WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' /* 2019-10-24 */ ELSE 'PROG' END STATUS , T1.PRIORITY , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T1.IDENTIFICATION_SUB_STATUS_PARTNER , T1.IDENTIFICATION_SUB_STATUS_SRC , T1.IDENTIFICATION_SUB_STATUS_BAT , T1.IDENTIFICATION_SUB_STATUS_BIN , T1.IDENTIFICATION_SUB_STATUS_ANDROID , IFNULL(T1.SRC_CSV_FILE_ID, '') SRC_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_CSV_FILE_ID, '') SRC_ANDROID_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_FILE_ID, '') SRC_ANDROID_NOTICE_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_XML_ID, '') SRC_ANDROID_NOTICE_XML_ID , IFNULL(T1.PACKAGE_FILE_ID, '') PACKAGE_FILE_ID , IFNULL(T1.NOTICE_FILE_ID, '') NOTICE_FILE_ID , T1.REVIEWER , T1.DIVISION , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.MODIFIER = USER_ID), T1.MODIFIER) AS MODIFIER , T1.MODIFIED_DATE FROM PROJECT_MASTER T1 WHERE T1.USE_YN = 'Y'
AND ( T1.CREATOR = #{userId} OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1
WHERE A1.PRJ_ID = T1.PRJ_ID AND (A1.USER_ID = #{userId} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{userId}) )) )
OR PUBLIC_YN = 'Y'
)
T1.PRJ_ID = #{item}
) A WHERE 1 = 1
AND A.IDENTIFICATION_STATUS != 'CONF'

