INSERT INTO
	OSS_COMPONENTS
	(
	REFERENCE_ID
	, REFERENCE_DIV
	, COMPONENT_IDX
	
	, OSS_ID
	
	, OSS_NAME
	, OSS_VERSION
	, DOWNLOAD_LOCATION
	, HOMEPAGE
	, FILE_PATH
	, EXCLUDE_YN
	, COPYRIGHT
	
	, BINARY_NAME
	
	
	, BINARY_SIZE
	
	
	, BINARY_NOTICE
	
	
	, CUSTOM_BINARY_YN
	
	
	, MERGE_PRE_DIV
	
	
	, OBLIGATION_TYPE
	
	
	, REF_COMPONENT_ID
	
	, REF_DIV
	, PRE_OBLIGATION_TYPE
	, ADMIN_CHECK_YN
	)
	VALUES
	(
	#{referenceId}
	, #{referenceDiv}
	, (SELECT IFNULL(MAX(OC.COMPONENT_IDX), 0) + 1 FROM OSS_COMPONENTS OC WHERE OC.REFERENCE_ID=#{referenceId} AND OC.REFERENCE_DIV=#{referenceDiv})		
	
	, (CASE WHEN #{ossId} = '' THEN NULL ELSE #{ossId} END)
	
	, #{ossName}
	, REPLACE(#{ossVersion}, 'N/A','')
	, #{downloadLocation}
	, #{homepage}
	, #{filePath}
	, #{excludeYn}
	, #{copyrightText}
	
	, #{binaryName}
	
	
	, #{binarySize}
	
	
	, #{binaryNotice}
	
	
	, #{customBinaryYn}
	
	
	, #{mergePreDiv}
	
	
	, #{obligationType}
	
	
	, #{refComponentId}
	
	
		
		, #{refDiv}
		
		
		, (SELECT OCP.REFERENCE_DIV FROM OSS_COMPONENTS OCP WHERE OCP.COMPONENT_ID = #{refComponentId})
		
	
	, #{preObligationType}
	, #{adminCheckYn}
	)

