mapper.select:
/* ProjectMapper.selectProjectMaster */ SELECT A.PRJ_ID , A.PRJ_NAME , A.PRJ_VERSION , A.DISTRIBUTION_TYPE , A.NETWORK_SERVER_TYPE , CASE WHEN B.CONTENTS IS NULL THEN A.COMMENT ELSE B.CONTENTS END AS COMMENT , B.COMM_ID AS COMMENT_IDX , A.OS_TYPE , A.OS_TYPE_ETC , A.USE_CUSTOM_NOTICE_YN , A.IDENTIFICATION_STATUS , A.VERIFICATION_STATUS , A.DESTRIBUTION_STATUS , A.IDENTIFICATION_SUB_STATUS_PARTNER , A.IDENTIFICATION_SUB_STATUS_SRC , A.IDENTIFICATION_SUB_STATUS_BAT , A.IDENTIFICATION_SUB_STATUS_BIN , A.IDENTIFICATION_SUB_STATUS_ANDROID , A.COMPLETE_YN , A.DROP_YN , A.REVIEWER , A.USE_YN , A.COPY_PRJ_ID , A.WITHOUT_VERIFY_YN
/* 다운로드 허용 플래그 */
, IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x01, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_HTML_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x02, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_TEXT_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x04, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_HTML_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x08, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_TEXT_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x10, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_SHEET_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x20, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_RDF_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x40, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_TAG_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x80, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_JSON_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x100, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_YAML_YN
/** DISTRIBUTION **/ , A.DISTRIBUTE_MASTER_CATEGORY , A.DISTRIBUTE_NAME , A.DISTRIBUTE_SOFTWARE_TYPE , A.DISTRIBUTE_TARGET , A.DISTRIBUTE_DEPLOY_YN , A.DISTRIBUTE_DEPLOY_TIME , A.DISTRIBUTE_OSD_KEY
/** FILE_ID **/ , A.DEP_CSV_FILE_ID , A.SRC_CSV_FILE_ID , A.SRC_ANDROID_CSV_FILE_ID , A.SRC_ANDROID_NOTICE_FILE_ID , A.SRC_ANDROID_NOTICE_XML_ID , A.SRC_ANDROID_RESULT_FILE_ID , A.PACKAGE_FILE_ID , A.PACKAGE_FILE_ID2 , A.PACKAGE_FILE_ID3 , A.NOTICE_FILE_ID , A.ZIP_FILE_ID , A.BIN_CSV_FILE_ID , A.BIN_BINARY_FILE_ID
, A.README_YN , A.README_CONTENT , A.README_FILE_NAME , A.VERIFY_FILE_CONTENT , A.EXCEPT_FILE_CONTENT , A.CREATOR , A.CREATED_DATE , A.MODIFIER , A.MODIFIED_DATE , C.USER_NAME PRJ_USER_NAME , R.USER_NAME REVIEWER_NAME , D.CD_DTL_NM PRJ_DIVISION_NAME , A.PUBLIC_YN , A.COMM_ID , A.STATUS_REQUEST_YN , A.STATUS_VERIFY_YN , A.CHANGED_NOTICE_YN , CASE WHEN A.COMPLETE_YN = 'Y' AND A.STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN A.DROP_YN = 'Y' AND A.STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN A.IDENTIFICATION_STATUS = 'REV' OR A.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN A.IDENTIFICATION_STATUS = 'REQ' OR A.VERIFICATION_STATUS = 'REQ' OR A.STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'CONF' AND A.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'CONF' AND A.DESTRIBUTION_STATUS = 'NA' THEN 'REV' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'NA' AND A.DESTRIBUTION_STATUS = 'NA' THEN 'REV' ELSE 'PROG' END STATUS , A.PRIORITY FROM PROJECT_MASTER A LEFT JOIN COMMENTS_HISTORY B ON A.COMMENT = B.COMM_ID LEFT OUTER JOIN T2_USERS C ON A.CREATOR = C.USER_ID
LEFT OUTER JOIN T2_USERS R ON A.REVIEWER = R.USER_ID
LEFT OUTER JOIN T2_CODE_DTL D ON A.DIVISION = D.CD_DTL_NO AND D.CD_NO = '200' AND D.USE_YN = 'Y' WHERE A.PRJ_ID = #{prjId}

SELECT T1.*, T2.NOTICE_TYPE, T2.NOTICE_TYPE_ETC,
/* 다운로드 허용 플래그 */
IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x01, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_HTML_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x02, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_TEXT_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x04, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_HTML_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x08, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_TEXT_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x10, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_SHEET_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x20, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_RDF_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x40, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_TAG_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x80, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_JSON_YN, IF(T1.ALLOW_DOWNLOAD_BIT_FLAG & 0x100, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_YAML_YN
FROM PROJECT_MASTER T1 LEFT OUTER JOIN OSS_NOTICE T2 ON T1.PRJ_ID = T2.PRJ_ID WHERE T1.USE_YN = 'Y' AND T1.PRJ_ID = #{prjId}

