mapper.select:
SELECT COUNT(1) FROM T2_CODE WHERE SYS_CD_YN != 'Y'
AND CD_NO = #{cdNo}
AND CD_NM LIKE CONCAT('%', #{cdNm}, '%')

SELECT CD_NO AS gridId ,CD_NO ,CD_NM ,CD_EXP FROM T2_CODE WHERE SYS_CD_YN != 'Y'
AND CD_NO = #{cdNo}
AND CD_NM LIKE CONCAT('%', #{cdNm}, '%')

mapper.update:
UPDATE T2_CODE_DTL
SET
	CD_DTL_NO = #{cdDtlNo},
	CD_DTL_NM = #{cdDtlNm},
	CD_DTL_EXP = #{cdDtlExp},
	CD_SUB_NO = #{cdSubNo},
	CD_ORDER = #{cdOrder},
	USE_YN = #{useYn}
WHERE CD_NO = #{cdNo}
AND CD_DTL_NO = #{cdDtlNoOrign}

mapper.delete:
DELETE
FROM T2_CODE_DTL
WHERE CD_NO = #{cdNo}
AND CD_DTL_NO = #{cdDtlNo}

mapper.update:
/* test 필요함. */
	UPDATE T2_CODE_DTL
	   SET #{columnNm1} = #{columnValue1}
	 WHERE CD_NO = #{cdNo}
