mapper.insert:
INSERT INTO
	COMMENTS_HISTORY
	(
		
		COMM_ID ,
		
		REFERENCE_ID
		, REFERENCE_DIV
		, STATUS
		, STATUS_CODE
		, CONTENTS
		, CREATOR
		, MODIFIER
		, EXPANSION1
	)
	VALUES
	(
		
		#{commId} ,
		
		#{referenceId}
		, #{referenceDiv}
		, #{status}
		, #{statusCode}
		, #{contents}
		, #{loginUserName}
		, #{loginUserName}
		, #{expansion1}
	) ON DUPLICATE KEY UPDATE
		
		COMM_ID  = #{commId},
		
		REFERENCE_ID = #{referenceId}
		, REFERENCE_DIV = #{referenceDiv}
		, CONTENTS = #{contents}
		, MODIFIER = #{loginUserName}

mapper.update:
UPDATE 
	COMMENTS_HISTORY
SET 
	USE_YN= 'N', MODIFIER = #{loginUserName}
WHERE 
	COMM_ID = #{commId}

UPDATE 
	COMMENTS_HISTORY
SET 
	USE_YN= 'N'
WHERE 
	USE_YN = 'Y'
	AND REFERENCE_ID = #{referenceId}
	AND REFERENCE_DIV = #{referenceDiv}
	AND MODIFIER = #{loginUserName}

UPDATE 
	COMMENTS_HISTORY
SET 
	USE_YN= 'N', MODIFIER = #{loginUserName}
WHERE 
	REFERENCE_ID = #{referenceId}
	AND REFERENCE_DIV = #{referenceDiv}

mapper.select:
SELECT CONTENTS FROM COMMENTS_HISTORY
WHERE
	USE_YN = 'Y'
	AND REFERENCE_ID = #{referenceId}
	AND REFERENCE_DIV = #{referenceDiv}
	AND CREATOR = #{loginUserName}
ORDER BY MODIFIED_DATE DESC
LIMIT 1

mapper.update:
UPDATE COMMENTS_HISTORY 
SET CONTENTS = #{contents}, MODIFIER = #{loginUserName}
WHERE COMM_ID = #{commId}

INSERT INTO COMMENTS_HISTORY_READ 
VALUES (
	#{commId}
	, #{referenceId}
	, #{referenceDiv}
	, 'Y'
	, #{loginUserName}
	, now())
ON DUPLICATE KEY UPDATE
	COMM_ID = #{commId}
	, REFERENCE_ID = #{referenceId}
	, REFERENCE_DIV = #{referenceDiv}
	, CREATOR = #{loginUserName}
