mapper.select:
SELECT CONCAT(SUBSTR(RESULT.PRJ_DIVISION, 1, 1), '-', RESULT.PRJ_ID) AS PRJ_DIVISION_ID , RESULT.PRJ_DIVISION , RESULT.PRJ_ID , RESULT.PRJ_NAME , RESULT.PRJ_VERSION , RESULT.STATUS , RESULT.STAGE , RESULT.OSS_NOTICE_DUE_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE RESULT.CREATOR = USER_ID), RESULT.CREATOR) AS CREATOR , RESULT.CREATED_DATE , RESULT.STAGE , RESULT.MODIFIER , RESULT.MODIFIED_DATE , IFNULL(IFNULL((SELECT USER_NAME FROM T2_USERS WHERE RESULT.REVIEWER = USER_ID), ''), RESULT.REVIEWER) AS REVIEWER_NAME , RESULT.REVIEWER AS REVIEWER , RESULT.PRIORITY FROM (
SELECT 'PRJ' AS PRJ_DIVISION , T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' ELSE 'PROG' END STATUS , CASE WHEN T1.STATUS_REQUEST_YN = 'Y' THEN 'B' WHEN (T1.DESTRIBUTION_STATUS != 'NA' OR T1.DESTRIBUTION_STATUS IS NULL) AND T1.VERIFICATION_STATUS IN ('CONF', 'NA') THEN 'D' WHEN T1.VERIFICATION_STATUS IS NOT NULL OR (T1.VERIFICATION_STATUS IS NULL AND T1.IDENTIFICATION_STATUS = 'CONF') THEN 'P' ELSE 'I' END STAGE , T1.OSS_NOTICE_DUE_DATE , T1.CREATOR , T1.CREATED_DATE , T1.MODIFIER , T1.MODIFIED_DATE , T1.REVIEWER , T1.PRIORITY FROM PROJECT_MASTER T1 WHERE T1.USE_YN = 'Y' AND (IFNULL(T1.DROP_YN, '') <> 'Y') AND (IFNULL(T1.COMPLETE_YN, '') <> 'Y' OR T1.STATUS_REQUEST_YN = 'Y') AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
)
UNION ALL
SELECT '3RD' AS PRJ_DIVISION , T1.PARTNER_ID AS PRJ_ID , CONCAT ('(', T1.PARTNER_NAME, ')', SOFTWARE_NAME) AS PRJ_NAME , SOFTWARE_VERSION AS PRJ_VERSION , T1.STATUS , '-' AS STAGE , '-' AS OSS_NOTICE_DUE_DATE , T1.CREATOR , T1.CREATED_DATE , T1.MODIFIER , T1.MODIFIED_DATE , T1.REVIEWER , '' AS PRIORITY FROM PARTNER_MASTER T1 WHERE T1.USE_YN = 'Y' AND T1.STATUS <> 'CONF'
AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PARTNER_WATCHER A1 WHERE A1.PARTNER_ID = T1.PARTNER_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
) ) RESULT WHERE 1 = 1 AND RESULT.STATUS IN ('PROG','REQ', 'REV', 'FREV')
ORDER BY CASE WHEN RESULT.STATUS = #{status} THEN 0 ELSE 1 END, RESULT.MODIFIED_DATE DESC
ORDER BY RESULT.MODIFIED_DATE DESC

SELECT COUNT(1) FROM (
SELECT T1.PRJ_ID , T1.CREATOR , T1.REVIEWER FROM PROJECT_MASTER T1 WHERE T1.USE_YN = 'Y' AND (IFNULL(T1.DROP_YN, '') <> 'Y') AND (IFNULL(T1.COMPLETE_YN, '') <> 'Y' OR T1.STATUS_REQUEST_YN = 'Y') AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
)
UNION ALL
SELECT T1.PARTNER_ID , T1.CREATOR , T1.REVIEWER FROM PARTNER_MASTER T1 WHERE T1.USE_YN = 'Y' AND T1.STATUS <> 'CONF' AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PARTNER_WATCHER A1 WHERE A1.PARTNER_ID = T1.PARTNER_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
)
) RESULT WHERE 1 = 1

SELECT RESULT.STATUS, COUNT(1) AS STATUS_CNT FROM (
SELECT T1.PRJ_ID , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' ELSE 'PROG' END STATUS FROM PROJECT_MASTER T1 WHERE T1.USE_YN = 'Y' AND (IFNULL(T1.DROP_YN, '') <> 'Y') AND (IFNULL(T1.COMPLETE_YN, '') <> 'Y' OR T1.STATUS_REQUEST_YN = 'Y') AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
)
UNION ALL
SELECT T1.PARTNER_ID AS PRJ_ID, T1.STATUS FROM PARTNER_MASTER T1 WHERE T1.USE_YN = 'Y' AND T1.STATUS <> 'CONF' AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PARTNER_WATCHER A1 WHERE A1.PARTNER_ID = T1.PARTNER_ID AND A1.USER_ID = #{loginUserName} )
OR T1.REVIEWER = #{loginUserName}
)
) RESULT WHERE 1 = 1 AND RESULT.STATUS IN ('PROG', 'REQ', 'REV', 'FREV') GROUP BY RESULT.STATUS

SELECT EML_MESSAGE
FROM EMAIL_SND_HIS 
WHERE SND_SEQ = #{sndSeq}
