INSERT
INTO USER_NOTICE
(
	TITLE
	, NOTICE
	, S_DATE
	, E_DATE
	, PUBLISH_YN
	, MODIFIER
	, MODIFIED_DATE
	, CREATOR
	, CREATED_DATE
)
VALUES
(
	#{title}
	,#{notice}
	,#{sDate}
	,#{eDate}
	,#{publishYn}
	,#{loginUserName}
	,now()
	,#{loginUserName}
	,now()
)

UPDATE USER_NOTICE
SET
	TITLE 			= #{title}
	, NOTICE		= #{notice}
	, S_DATE		= #{sDate}
	, E_DATE		= #{eDate}
	, PUBLISH_YN	= #{publishYn}
	, MODIFIER		= #{loginUserName}
	, MODIFIED_DATE	= now()
WHERE SEQ			= #{seq}
