/* OssMapper.getOssListByName */ SELECT T1.OSS_ID, T1.OSS_NAME, T1.OSS_VERSION, T1.LICENSE_DIV, T1.DOWNLOAD_LOCATION, T1.HOMEPAGE, T1.SUMMARY_DESCRIPTION, T1.ATTRIBUTION, T1.COPYRIGHT , (SELECT GROUP_CONCAT(D.DOWNLOAD_LOCATION SEPARATOR ',') FROM OSS_DOWNLOADLOCATION D WHERE D.OSS_ID = T1.OSS_ID ) AS DOWNLOAD_LOCATION_GROUP , T1.OBLIGATION_TYPE, T1.LICENSE_TYPE , T2.LICENSE_ID, T2.OSS_LICENSE_IDX, T2.OSS_LICENSE_COMB, T2.OSS_COPYRIGHT , CASE WHEN IFNULL(T3.SHORT_IDENTIFIER, '') <> '' THEN T3.SHORT_IDENTIFIER ELSE T3.LICENSE_NAME END AS LICENSE_NAME , T3.LICENSE_TEXT AS OSS_LICENSE_TEXT , (SELECT GROUP_CONCAT(OSS_NICKNAME SEPARATOR '|') FROM OSS_NICKNAME WHERE OSS_NAME = T1.OSS_NAME) AS OSS_NICKNAME , CONCAT((SELECT USER_NAME FROM T2_USERS WHERE USER_ID = T1.CREATOR), ' (', DATE_FORMAT(T1.CREATED_DATE, '%Y-%m-%d'), ')') AS CREATOR , CONCAT((SELECT USER_NAME FROM T2_USERS WHERE USER_ID = T1.MODIFIER), ' (', DATE_FORMAT(T1.MODIFIED_DATE, '%Y-%m-%d'), ')') AS MODIFIER , SUB1.OSS_TYPE , (SELECT GROUP_CONCAT(IF(LM.SHORT_IDENTIFIER = '' OR LM.SHORT_IDENTIFIER IS NULL , LM.LICENSE_NAME , LM.SHORT_IDENTIFIER) ORDER BY OSS_LICENSE_IDX ASC SEPARATOR ', ') FROM OSS_LICENSE_DETECTED ODT INNER JOIN LICENSE_MASTER LM ON ODT.LICENSE_ID = LM.LICENSE_ID WHERE ODT.OSS_ID = T1.OSS_ID) AS DETECTED_LICENSE FROM OSS_MASTER T1 INNER JOIN (SELECT OSS_ID, CONCAT(IF(MULTI_LICENSE_FLAG = 'N', '0', '1'), IF(DUAL_LICENSE_FLAG = 'N', '0', '1'), IF(VERSION_DIFF_FLAG = 'N', '0', '1') ) AS OSS_TYPE FROM OSS_MASTER_LICENSE_FLAG ) SUB1 ON T1.OSS_ID = SUB1.OSS_ID INNER JOIN OSS_LICENSE_DECLARED T2 ON T1.OSS_ID = T2.OSS_ID INNER JOIN LICENSE_MASTER T3 ON T2.LICENSE_ID = T3.LICENSE_ID WHERE T1.USE_YN = 'Y'
AND T1.OSS_ID = #{ossId}
AND ( T1.OSS_NAME = #{ossName} OR EXISTS (SELECT 1 FROM OSS_NICKNAME A1 WHERE A1.OSS_NAME = T1.OSS_NAME AND A1.OSS_NICKNAME = #{ossName}) )
ORDER BY OSS_VERSION DESC, OSS_LICENSE_IDX

