mapper.select:
SELECT T1.OSS_ID , CASE WHEN T2.OSS_NICKNAME IS NULL THEN T1.OSS_NAME ELSE CONCAT(T1.OSS_NAME, '(', T2.OSS_NICKNAME, ')') END AS OSS_NAME , T1.OSS_NAME AS OSS_NAME_TEMP , T1.OSS_VERSION FROM OSS_MASTER T1 LEFT OUTER JOIN ( SELECT A1.OSS_NAME, GROUP_CONCAT(A1.OSS_NICKNAME SEPARATOR ' / ') AS OSS_NICKNAME FROM OSS_NICKNAME A1 GROUP BY OSS_NAME ) T2 ON T1.OSS_NAME = T2.OSS_NAME WHERE T1.USE_YN = 'Y' AND T1.OSS_ID <> #{ossId}
AND T1.OSS_NAME LIKE CONCAT('%',#{schOssName},'%')
ORDER BY T1.OSS_NAME, T1.OSS_VERSION DESC

SELECT T1.OSS_ID , T1.OSS_NAME , T1.OSS_VERSION , T1.LICENSE_DIV , T1.DOWNLOAD_LOCATION , T1.HOMEPAGE , T1.SUMMARY_DESCRIPTION , T1.ATTRIBUTION , T1.LICENSE_TYPE , T1.OBLIGATION_TYPE , T1.COPYRIGHT , T2.OSS_COPYRIGHT , SUB1.OSS_TYPE FROM OSS_MASTER T1 LEFT OUTER JOIN OSS_LICENSE T2 ON T1.OSS_ID = T2.OSS_ID AND T2.OSS_LICENSE_IDX = 1
INNER JOIN ( SELECT OSS_ID, CONCAT(IF(MULTI_LICENSE_FLAG = 'N', '0', '1'), IF(DUAL_LICENSE_FLAG = 'N', '0', '1'), IF(VERSION_DIFF_FLAG = 'N', '0', '1')) AS OSS_TYPE FROM OSS_MASTER_LICENSE_FLAG) SUB1 ON T1.OSS_ID = SUB1.OSS_ID
WHERE T1.USE_YN = 'Y' AND T1.OSS_ID = #{ossId}

SELECT T1.OSS_ID , T1.OSS_NAME , T1.OSS_VERSION , T1.HOMEPAGE , T1.DOWNLOAD_LOCATION , T1.SUMMARY_DESCRIPTION , T1.ATTRIBUTION , T1.CREATOR , T1.MODIFIER , T1.CREATED_DATE , T1.MODIFIED_DATE , T3.LICENSE_NAME , T4.CD_DTL_NM AS LICENSE_TYPE , CONCAT( IF( T3.OBLIGATION_NOTIFICATION_YN='Y' ,'Y', 'N') , IF( T3.OBLIGATION_DISCLOSING_SRC_YN='Y' ,'Y', 'N') , IF( T3.OBLIGATION_NEEDS_CHECK_YN='Y' ,'Y', 'N') ) AS OBLIGATION FROM OSS_MASTER T1 LEFT OUTER JOIN OSS_LICENSE T2 ON T1.OSS_ID = T2.OSS_ID /*OSS License Table*/ LEFT OUTER JOIN LICENSE_MASTER T3 ON T2.LICENSE_ID = T3.LICENSE_ID AND T3.USE_YN = 'Y' /*License Master Table*/ LEFT OUTER JOIN T2_CODE_DTL T4 ON T3.LICENSE_TYPE = T4.CD_DTL_NO /*Code Table*/ LEFT OUTER JOIN OSS_NICKNAME T5 ON T1.OSS_NAME = T5.OSS_NAME /*OSS Nickname Table*/
LEFT OUTER JOIN OSS_DOWNLOADLOCATION DOWN ON T1.OSS_ID = DOWN.OSS_ID
WHERE T1.USE_YN = 'Y' AND T4.CD_NO = '201' /*검색 쿼리 시작*/
AND T1.OSS_NAME = #{ossName} OR T5.OSS_NICKNAME = #{ossName}
AND UPPER(T3.LICENSE_NAME) = UPPER(#{licenseName}) OR UPPER(T3.LICENSE_NAME) IN (select UPPER(LICENSE_NAME) from LICENSE_NICKNAME where UPPER(LICENSE_NICKNAME) = UPPER(#{licenseName}) ) OR UPPER(T3.SHORT_IDENTIFIER) = UPPER(#{licenseName})
AND T1.SUMMARY_DESCRIPTION LIKE CONCAT('%',#{summaryDescription},'%')
AND ( T1.HOMEPAGE LIKE CONCAT('%',#{homepage},'%') OR T1.DOWNLOAD_LOCATION LIKE CONCAT('%',#{homepage},'%')
OR DOWN.DOWNLOAD_LOCATION LIKE CONCAT('%',#{homepage},'%')
)
T2.OSS_COPYRIGHT LIKE CONCAT('%',#{copyright},'%')
AND T1.CREATOR LIKE CONCAT('%',#{creator},'%')
AND T1.MODIFIER LIKE CONCAT('%',#{modifier},'%')
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= #{cStartDate}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= #{cEndDate}
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') >= #{mStartDate}
AND DATE_FORMAT(T1.MODIFIED_DATE,'%Y%m%d') <= #{mEndDate}
/*검색 쿼리 끝*/ GROUP BY T1.OSS_ID

/* OssMapper.getBasicOssInfoListById */ SELECT T1.OSS_ID , T1.OSS_NAME , T1.OSS_VERSION , T1.LICENSE_DIV , IFNULL((SELECT GROUP_CONCAT(D.DOWNLOAD_LOCATION ORDER BY D.SORT_ORDER ASC) FROM OSS_DOWNLOADLOCATION D WHERE D.OSS_ID = T1.OSS_ID), T1.DOWNLOAD_LOCATION) AS DOWNLOAD_LOCATION , T1.HOMEPAGE , T1.SUMMARY_DESCRIPTION , T1.ATTRIBUTION , T1.COPYRIGHT , T1.MODIFIER , T1.MODIFIED_DATE , T1.CREATOR , T1.CREATED_DATE , T1.LICENSE_TYPE AS OSS_LICENSE_TYPE , T1.OBLIGATION_TYPE , T2.LICENSE_ID , T2.OSS_LICENSE_IDX , T2.OSS_LICENSE_COMB , T3.LICENSE_TEXT AS OSS_LICENSE_TEXT , T2.OSS_COPYRIGHT , T3.LICENSE_NAME , T3.LICENSE_TYPE , T3.OBLIGATION_DISCLOSING_SRC_YN , T3.OBLIGATION_NOTIFICATION_YN , T3.OBLIGATION_NEEDS_CHECK_YN , T3.SHORT_IDENTIFIER , SUB1.OSS_TYPE FROM OSS_MASTER T1 INNER JOIN OSS_LICENSE T2 ON T1.OSS_ID = T2.OSS_ID INNER JOIN LICENSE_MASTER T3 ON T2.LICENSE_ID = T3.LICENSE_ID AND T3.USE_YN = 'Y' INNER JOIN (SELECT OSS_ID, CONCAT(IF(MULTI_LICENSE_FLAG = 'N', '0', '1'), IF(DUAL_LICENSE_FLAG = 'N', '0', '1'), IF(VERSION_DIFF_FLAG = 'N', '0', '1') ) AS OSS_TYPE FROM OSS_MASTER_LICENSE_FLAG ) SUB1 ON T1.OSS_ID = SUB1.OSS_ID WHERE T1.USE_YN = 'Y'
#{_ossid}
ORDER BY OSS_ID, OSS_LICENSE_IDX

/* OssMapper.getBasicOssInfoList */ SELECT T1.OSS_ID , T1.OSS_NAME , T1.OSS_NAME AS OSS_NAME_TEMP , T1.OSS_VERSION , T1.LICENSE_DIV , T1.DOWNLOAD_LOCATION , T1.HOMEPAGE , T1.SUMMARY_DESCRIPTION , T1.ATTRIBUTION , T1.COPYRIGHT , T1.LICENSE_TYPE AS OSS_LICENSE_TYPE , T1.OBLIGATION_TYPE , T2.LICENSE_ID , T2.OSS_LICENSE_IDX , T2.OSS_LICENSE_COMB , CASE WHEN IFNULL(T2.OSS_LICENSE_TEXT, '') = '' THEN T3.LICENSE_TEXT ELSE T2.OSS_LICENSE_TEXT END AS OSS_LICENSE_TEXT , T2.OSS_COPYRIGHT , T3.LICENSE_NAME , T3.LICENSE_TYPE , T3.OBLIGATION_DISCLOSING_SRC_YN , T3.OBLIGATION_NOTIFICATION_YN , T3.OBLIGATION_NEEDS_CHECK_YN , T3.SHORT_IDENTIFIER , T5.OSS_NICKNAME , T6.MULTI_LICENSE_FLAG , T6.DUAL_LICENSE_FLAG , T6.VERSION_DIFF_FLAG FROM OSS_MASTER T1 INNER JOIN OSS_LICENSE T2 ON T1.OSS_ID = T2.OSS_ID INNER JOIN LICENSE_MASTER T3 ON T2.LICENSE_ID = T3.LICENSE_ID AND T3.USE_YN = 'Y' LEFT OUTER JOIN (SELECT OSS_NAME, GROUP_CONCAT(OSS_NICKNAME separator '|') AS OSS_NICKNAME FROM OSS_NICKNAME GROUP BY OSS_NAME) T5 ON T1.OSS_NAME = T5.OSS_NAME LEFT OUTER JOIN OSS_MASTER_LICENSE_FLAG T6 ON T1.OSS_ID = T6.OSS_ID WHERE T1.USE_YN = 'Y'
#{_ossName}
UNION SELECT T1.OSS_ID , T4.OSS_NICKNAME AS OSS_NAME , T1.OSS_NAME AS OSS_NAME_TEMP , T1.OSS_VERSION , T1.LICENSE_DIV , T1.DOWNLOAD_LOCATION , T1.HOMEPAGE , T1.SUMMARY_DESCRIPTION , T1.ATTRIBUTION , T1.COPYRIGHT , T1.LICENSE_TYPE AS OSS_LICENSE_TYPE , T1.OBLIGATION_TYPE , T2.LICENSE_ID , T2.OSS_LICENSE_IDX , T2.OSS_LICENSE_COMB , CASE WHEN IFNULL(T2.OSS_LICENSE_TEXT, '') = '' THEN T3.LICENSE_TEXT ELSE T2.OSS_LICENSE_TEXT END AS OSS_LICENSE_TEXT , T2.OSS_COPYRIGHT , T3.LICENSE_NAME , T3.LICENSE_TYPE , T3.OBLIGATION_DISCLOSING_SRC_YN , T3.OBLIGATION_NOTIFICATION_YN , T3.OBLIGATION_NEEDS_CHECK_YN , T3.SHORT_IDENTIFIER , T5.OSS_NICKNAME , T6.MULTI_LICENSE_FLAG , T6.DUAL_LICENSE_FLAG , T6.VERSION_DIFF_FLAG FROM OSS_MASTER T1 INNER JOIN OSS_LICENSE T2 ON T1.OSS_ID = T2.OSS_ID INNER JOIN LICENSE_MASTER T3 ON T2.LICENSE_ID = T3.LICENSE_ID AND T3.USE_YN = 'Y' INNER JOIN OSS_NICKNAME T4 ON T1.OSS_NAME = T4.OSS_NAME LEFT OUTER JOIN (SELECT OSS_NAME, GROUP_CONCAT(OSS_NICKNAME separator '|') AS OSS_NICKNAME FROM OSS_NICKNAME GROUP BY OSS_NAME) T5 ON T1.OSS_NAME = T5.OSS_NAME LEFT OUTER JOIN OSS_MASTER_LICENSE_FLAG T6 ON T1.OSS_ID = T6.OSS_ID WHERE T1.USE_YN = 'Y'
#{_ossName}
ORDER BY OSS_NAME, OSS_VERSION, OSS_LICENSE_IDX

