mapper.select:
SELECT OSS_ID as REF_OSS_ID, 'oscAdmin' as CREATOR FROM OSS_MASTER 
WHERE USE_YN = 'Y' AND IFNULL(OSS_VERSION, '') = #{ossVersion} 
	AND OSS_NAME IN

		#{_ossName}

SELECT PRODUCT AS OSS_NAME, CVSS_SCORE, CVE_ID, VERSION as OSS_VERSION, MODI_DATE AS VULN_DATE FROM NVD_DATA_SCORE_V3 WHERE
#{_ossName}
AND VERSION = #{ossVersion} ORDER BY CVSS_SCORE DESC, CVE_ID LIMIT 1

SELECT T2.PRODUCT AS OSS_NAME, T2.VERSION AS OSS_VERSION, T2.CVE_ID, T2.CVSS_SCORE, T2.MODI_DATE AS VULN_DATE FROM ( SELECT PRODUCT, MAX(CVSS_SCORE) AS CVSS_SCORE FROM NVD_DATA_SCORE_V3 WHERE
#{_ossName}
GROUP BY PRODUCT ) T1 INNER JOIN NVD_DATA_SCORE_V3 T2 ON T1.PRODUCT = T2.PRODUCT AND T1.CVSS_SCORE = T2.CVSS_SCORE ORDER BY CVE_ID LIMIT 1

SELECT A.OSS_NAME , MIN(A.S_ORDER) AS S_ORDER FROM ( SELECT T1.OSS_ID , T1.OSS_NAME , (CASE WHEN IFNULL(T2.DOWNLOAD_LOCATION, T1.DOWNLOAD_LOCATION) = #{downloadLocation} THEN 1 WHEN IFNULL(T2.DOWNLOAD_LOCATION, T1.DOWNLOAD_LOCATION) LIKE CONCAT(#{downloadLocation},'%') THEN 2 ELSE 3 END) AS S_ORDER FROM OSS_MASTER T1 LEFT JOIN OSS_DOWNLOADLOCATION T2 ON T1.OSS_ID = T2.OSS_ID WHERE T1.USE_YN = 'Y' AND IFNULL(T2.DOWNLOAD_LOCATION, T1.DOWNLOAD_LOCATION) LIKE CONCAT('%',#{downloadLocation},'%') ) AS A WHERE 1 = 1
AND A.OSS_NAME != #{ossName}
GROUP BY A.OSS_NAME ORDER BY MIN(A.S_ORDER) LIMIT 10

SELECT T1.OSS_NAME , MIN(T1.S_ORDER) AS S_ORDER FROM ( SELECT OSS_ID , OSS_NAME , (CASE WHEN HOMEPAGE = #{homepage} THEN 1 WHEN HOMEPAGE LIKE CONCAT(#{homepage},'%') THEN 2 ELSE 3 END) AS S_ORDER FROM OSS_MASTER WHERE USE_YN = 'Y' AND HOMEPAGE LIKE CONCAT('%',#{homepage},'%') ) AS T1 WHERE 1 = 1
AND T1.OSS_NAME != #{ossName}
GROUP BY T1.OSS_NAME ORDER BY MIN(T1.S_ORDER) LIMIT 10

SELECT T1.OSS_NAME , MIN(T1.S_ORDER) AS S_ORDER FROM ( SELECT OSS_ID , OSS_NAME , (CASE WHEN DOWNLOAD_LOCATION = #{downloadLocation} THEN 1 WHEN DOWNLOAD_LOCATION LIKE CONCAT(#{downloadLocation},'%') THEN 2 ELSE 3 END) AS S_ORDER FROM OSS_MASTER WHERE USE_YN = 'Y' AND DOWNLOAD_LOCATION LIKE CONCAT('%',#{downloadLocation},'%')
AND OSS_NAME <> #{ossName}
) AS T1 GROUP BY T1.OSS_NAME ORDER BY MIN(T1.S_ORDER) LIMIT 10

SELECT T1.OSS_NAME , MIN(T1.S_ORDER) AS S_ORDER FROM ( SELECT OSS_ID , OSS_NAME , (CASE WHEN HOMEPAGE = #{homepage} THEN 1 WHEN HOMEPAGE LIKE CONCAT(#{homepage},'%') THEN 2 ELSE 3 END) AS S_ORDER FROM OSS_MASTER WHERE USE_YN = 'Y' AND HOMEPAGE LIKE CONCAT('%',#{homepage},'%')
AND OSS_NAME <> #{ossName}
) AS T1 GROUP BY T1.OSS_NAME ORDER BY MIN(T1.S_ORDER) LIMIT 10

