mapper.insert:
INSERT INTO OSS_ANALYSIS_MAP ( COMPONENT_ID , REFERENCE_ID , OSS_NAME , OSS_VERSION , LICENSE_NAME , DOWNLOAD_LOCATION , HOMEPAGE , ANALYSIS_YN , COMPLETE_YN ) SELECT OC.COMPONENT_ID , #{prjId} AS REFERENCE_ID , OC.OSS_NAME , OC.OSS_VERSION , GROUP_CONCAT(DISTINCT OCL.LICENSE_NAME SEPARATOR ' AND ') AS LICENSE_NAME , OC.DOWNLOAD_LOCATION , OC.HOMEPAGE , 'N' AS ANALYSIS_YN , 'N' AS COMPLETE_YN FROM OSS_COMPONENTS OC LEFT JOIN OSS_COMPONENTS_LICENSE OCL ON OC.COMPONENT_ID = OCL.COMPONENT_ID WHERE OC.REFERENCE_ID = #{prjId} AND OC.REFERENCE_DIV = '13' AND OC.DOWNLOAD_LOCATION IS NOT NULL AND OC.DOWNLOAD_LOCATION != ''
#{id}
GROUP BY CONCAT(OSS_NAME, '-', OSS_VERSION)

