mapper.select:
SELECT COUNT(*) FROM (SELECT t1.PARTNER_ID FROM PARTNER_MASTER t1 LEFT JOIN T2_USERS t2 ON t1.CREATOR = t2.USER_ID
LEFT JOIN OSS_COMPONENTS t4 ON t1.PARTNER_ID = t4.REFERENCE_ID AND t4.REFERENCE_DIV = '20' LEFT JOIN OSS_COMPONENTS_LICENSE t5 ON t4.COMPONENT_ID = t5.COMPONENT_ID WHERE t1.USE_YN = 'Y'
AND (t1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PARTNER_WATCHER A1 WHERE A1.PARTNER_ID = t1.PARTNER_ID AND (A1.USER_ID = #{loginUserName} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{loginUserName})) ) )
OR t1.PUBLIC_YN = 'Y'
)
AND t1.PARTNER_ID = #{partnerId}
AND t4.OSS_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{ossName}, '_', '\\\\_'),'%')
AND t4.OSS_VERSION LIKE CONCAT('%',#{ossVersion},'%')
AND t5.LICENSE_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{licenseName}, '_', '\\\\_'),'%')
AND t1.STATUS = #{status}
#{item}
AND t1.PARTNER_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{partnerName}, '_', '\\\\_'),'%')
AND t1.SOFTWARE_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{softwareName}, '_', '\\\\_'),'%')
AND t1.SOFTWARE_VERSION LIKE CONCAT(REGEXP_REPLACE(#{softwareVersion}, '_', '\\\\_'),'%')
AND DATE_FORMAT(t1.CREATED_DATE,'%Y%m%d') >= #{createdDate1}
AND DATE_FORMAT(t1.CREATED_DATE,'%Y%m%d') <= #{createdDate2}
AND t1.DIVISION = #{division}
AND t1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%'))
AND t1.MODIFIER LIKE CONCAT('%',#{modifier},'%')
AND t1.REVIEWER IS NULL
AND t1.REVIEWER IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{reviewer},'%'))
AND t4.FILE_PATH LIKE CONCAT('%',REGEXP_REPLACE(#{binaryName}, '_', '\\\\_'))
GROUP BY t1.PARTNER_ID) T1

