mapper.insert:
INSERT INTO
	OSS_COMPONENTS
	(
	REFERENCE_ID
	, REFERENCE_DIV
	, COMPONENT_IDX
	
	, OSS_ID
	
	, OSS_NAME
	, OSS_VERSION
	, DOWNLOAD_LOCATION
	, HOMEPAGE
	, FILE_PATH
	, EXCLUDE_YN
	
	, COPYRIGHT
	
	
	, BINARY_NAME
	
	
	, BINARY_SIZE
	
	
	, BINARY_NOTICE
	
	
	, CUSTOM_BINARY_YN
	
	, REF_PARTNER_ID
	, REPORT_FILE_ID
	, PRE_OBLIGATION_TYPE
	, ADMIN_CHECK_YN
	)
	VALUES
	(
	#{referenceId}
	, #{referenceDiv}
	, (CASE WHEN IFNULL(#{componentIdx}, '') = '' THEN (SELECT IFNULL(MAX(COMPONENT_IDX), 0) + 1 FROM OSS_COMPONENTS OC WHERE REFERENCE_ID=#{referenceId} AND REFERENCE_DIV=#{referenceDiv}) ELSE #{componentIdx} END)
	
	, (CASE WHEN #{ossId} = '' THEN NULL ELSE #{ossId} END)
	
	, #{ossName}
	, REPLACE(#{ossVersion}, 'N/A','')
	, #{downloadLocation}
	, #{homepage}
	, #{filePath}
	, #{excludeYn}
	
	, #{copyright}
	
	
	, #{binaryName}
	
	
	, #{binarySize}
	
	
	, #{binaryNotice}
	
	
	, #{customBinaryYn}
	
	, #{refPartnerId}
	, #{reportFileId}
	, #{preObligationType}
	, #{adminCheckYn}
	)

