		, FILE_PATH = #{filePath}
		, EXCLUDE_YN = #{excludeYn}
		, BINARY_NAME = #{binaryName}
		, BINARY_NOTICE = #{binaryNotice}
		, CUSTOM_BINARY_YN = #{customBinaryYn}
