mapper.select:
/* ProjectMapper.selectProjectTotalCount */ SELECT COUNT(*) FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.DISTRIBUTION_TYPE , T1.OSS_NOTICE_DUE_DATE , T1.COMMENT , T1.OS_TYPE , T1.OS_TYPE_ETC , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' ELSE 'PROG' END STATUS , T1.PRIORITY , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T1.IDENTIFICATION_SUB_STATUS_PARTNER , T1.IDENTIFICATION_SUB_STATUS_SRC , T1.IDENTIFICATION_SUB_STATUS_BAT , T1.IDENTIFICATION_SUB_STATUS_BIN , T1.IDENTIFICATION_SUB_STATUS_ANDROID , IFNULL(T1.SRC_CSV_FILE_ID, '') SRC_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_CSV_FILE_ID, '') SRC_ANDROID_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_FILE_ID, '') SRC_ANDROID_NOTICE_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_XML_ID, '') SRC_ANDROID_NOTICE_XML_ID , IFNULL(T1.PACKAGE_FILE_ID, '') PACKAGE_FILE_ID , IFNULL(T1.NOTICE_FILE_ID, '') NOTICE_FILE_ID , T1.REVIEWER , T1.DIVISION , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.MODIFIER = USER_ID), T1.MODIFIER) AS MODIFIER , T1.MODIFIED_DATE FROM PROJECT_MASTER T1 LEFT JOIN OSS_NOTICE T2 ON T1.PRJ_ID = T2.PRJ_ID WHERE T1.USE_YN = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_PARTNER = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_DEP = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_SRC= 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_BAT = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_BIN = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_ANDROID = 'Y'
AND T1.IDENTIFICATION_STATUS = #{identificationStatus}
AND ( T1.CREATOR = #{loginUserName}
OR T1.REVIEWER = #{loginUserName}
OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} ) )
AND T1.PRJ_ID = #{prjId}
AND T1.PRJ_NAME LIKE CONCAT('%', REGEXP_REPLACE(#{prjName}, '_', '\\\\_'),'%')
AND (T1.PRJ_ID = #{prjIdName} OR T1.PRJ_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{prjIdName}, '_', '\\\\_'),'%'))
AND T1.PRJ_VERSION LIKE CONCAT(REGEXP_REPLACE(#{prjVersion}, '_', '\\\\_'),'%')
AND T1.DISTRIBUTION_TYPE = #{distributionType}
AND T2.NOTICE_TYPE = #{noticeType}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= #{schEndDate}
AND T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%'))
AND T1.REVIEWER IS NULL
AND T1.REVIEWER IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{reviewer},'%'))
AND T1.PRJ_ID IN ( SELECT A.PRJ_ID AS WATCHER FROM PROJECT_WATCHER A WHERE T1.PRJ_ID = A.PRJ_ID AND (A.USER_ID LIKE CONCAT('%',#{watcher},'%') OR A.EMAIL LIKE CONCAT('%',#{watcher},'%')))
AND EXISTS (SELECT 1 FROM ( SELECT DISTINCT REFERENCE_ID FROM OSS_COMPONENTS X
LEFT OUTER JOIN OSS_COMPONENTS_LICENSE Y ON X.COMPONENT_ID = Y.COMPONENT_ID LEFT OUTER JOIN LICENSE_MASTER Z ON Y.LICENSE_ID = Z.LICENSE_ID
WHERE X.EXCLUDE_YN <> 'Y'
AND Y.EXCLUDE_YN <> 'Y'
AND X.REFERENCE_DIV != '20'
AND X.OSS_NAME = #{ossName}
AND X.OSS_VERSION = #{ossVersion}
AND (Y.LICENSE_NAME = #{licenseName} OR Z.LICENSE_NAME = #{licenseName} OR Z.SHORT_IDENTIFIER = #{licenseName})
AND X.REF_PARTNER_ID IN (SELECT P.PARTNER_ID FROM PARTNER_MASTER P WHERE P.USE_YN = 'Y' AND P.STATUS = 'CONF' AND P.PARTNER_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{refPartnerName}, '_', '\\\\_'),'%'))
) TBL WHERE TBL.REFERENCE_ID = T1.PRJ_ID )
AND EXISTS(SELECT 1 FROM BINARY_MASTER X LEFT OUTER JOIN T2_FILE Y ON Y.FILE_SEQ = X.BINARY_FILE_ID WHERE X.BAT_STATUS = '60' AND T1.PRJ_ID = X.PRJ_ID AND Y.ORIG_NM LIKE CONCAT('%',REGEXP_REPLACE(#{binaryName}, '_', '\\\\_'),'%'))
AND EXISTS ( SELECT 1 FROM OSS_COMPONENTS SUB_B1 INNER JOIN PROJECT_MASTER SUB_B2 ON SUB_B2.PRJ_ID = SUB_B1.REFERENCE_ID AND SUB_B2.USE_YN = 'Y' WHERE T1.PRJ_ID = SUB_B2.PRJ_ID AND SUB_B1.EXCLUDE_YN != 'Y' AND SUB_B1.REFERENCE_DIV IN ('10','11','12','14','15') AND SUB_B1.BINARY_NAME LIKE CONCAT('%', #{schBinaryName},'%') )
AND EXISTS (SELECT 1 FROM PROJECT_MODEL M WHERE T1.PRJ_ID = M.PRJ_ID AND MODEL_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{modelName}, '_', '\\\\_'),'%'))
AND T1.COMMENT LIKE CONCAT('%', #{comment}, '%')
AND EXISTS( SELECT 1 FROM COMMENTS_HISTORY COMM WHERE COMM.USE_YN = 'Y' AND COMM.REFERENCE_ID = T1.PRJ_ID AND COMM.REFERENCE_DIV IN ('19', '10', '12', '14') AND REGEXP_REPLACE(COMM.CONTENTS, '<img[^>]+>', '') LIKE CONCAT('%',#{userComment},'%') )
AND T1.NETWORK_SERVER_TYPE = #{networkServerType}
AND T1.SECMAIL_YN = #{secMailYn}
) A WHERE 1=1
AND EXISTS (SELECT 1 FROM T2_CODE_DTL S WHERE S.CD_NO = '200' AND S.CD_DTL_NM LIKE CONCAT('%', #{prjDivision}, '%') AND A.DIVISION = S.CD_DTL_NO)
AND
A.STATUS = #{item}
AND A.PRIORITY = #{priority}

/* ProjectMapper.selectProjectList */ SELECT * FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.DISTRIBUTION_TYPE , T1.OSS_NOTICE_DUE_DATE , T1.COMMENT , T1.OS_TYPE , T1.OS_TYPE_ETC , CASE WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' ELSE 'PROG' END STATUS , T1.PRIORITY , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T1.IDENTIFICATION_SUB_STATUS_PARTNER , T1.IDENTIFICATION_SUB_STATUS_DEP , T1.IDENTIFICATION_SUB_STATUS_SRC , CASE WHEN IFNULL(IDENTIFICATION_SUB_STATUS_BAT, 'Y') = 'N' THEN 'N'
CASE WHEN IFNULL(IDENTIFICATION_SUB_STATUS_BAT, 'Y') = 'Y' AND EXISTS ( SELECT 1 FROM BINARY_MASTER ST1 WHERE ST1.USE_YN = 'Y' AND ST1.PRJ_ID = T1.PRJ_ID ) THEN 'Y'
END AS IDENTIFICATION_SUB_STATUS_BAT , T1.IDENTIFICATION_SUB_STATUS_BIN , T1.IDENTIFICATION_SUB_STATUS_ANDROID , IFNULL(T1.SRC_CSV_FILE_ID, '') SRC_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_CSV_FILE_ID, '') SRC_ANDROID_CSV_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_FILE_ID, '') SRC_ANDROID_NOTICE_FILE_ID , IFNULL(T1.SRC_ANDROID_NOTICE_XML_ID, '') SRC_ANDROID_NOTICE_XML_ID , IFNULL(T1.SRC_ANDROID_RESULT_FILE_ID, '') SRC_ANDROID_RESULT_FILE_ID , CASE WHEN T1.VERIFICATION_STATUS = 'CONF' THEN IFNULL(T1.PACKAGE_FILE_ID, '') ELSE '' END AS PACKAGE_FILE_ID , CASE WHEN T1.VERIFICATION_STATUS = 'CONF' THEN IFNULL(T1.PACKAGE_FILE_ID2, '') ELSE '' END AS PACKAGE_FILE_ID2 , CASE WHEN T1.VERIFICATION_STATUS = 'CONF' THEN IFNULL(T1.PACKAGE_FILE_ID3, '') ELSE '' END AS PACKAGE_FILE_ID3 , CASE WHEN T1.VERIFICATION_STATUS = 'CONF' THEN IFNULL(T1.PACKAGE_FILE_ID4, '') ELSE '' END AS PACKAGE_FILE_ID4 , CASE WHEN T1.VERIFICATION_STATUS = 'CONF' THEN IFNULL(T1.NOTICE_FILE_ID, '') ELSE '' END AS NOTICE_FILE_ID , CASE WHEN T1.IDENTIFICATION_STATUS = 'CONF' THEN IFNULL(T1.REVIEW_REPORT_FILE_ID, '') ELSE '' END AS REVIEW_REPORT_FILE_ID , T1.REVIEWER , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.REVIEWER = USER_ID), T1.REVIEWER) AS REVIEWER_NAME , T1.DIVISION , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.MODIFIER = USER_ID), T1.MODIFIER) AS MODIFIER , T1.MODIFIED_DATE , 'N' AS VULN_YN , 0 AS CVSS_SCORE , (SELECT COUNT(1) FROM OSS_COMPONENTS OC INNER JOIN OSS_COMPONENTS_LICENSE OCL ON OC.COMPONENT_ID = OCL.COMPONENT_ID AND OCL.EXCLUDE_YN <> 'Y' LEFT OUTER JOIN LICENSE_MASTER LM ON OCL.LICENSE_ID = LM.LICENSE_ID AND LM.USE_YN = 'Y' WHERE OC.REFERENCE_ID = T1.PRJ_ID AND OC.REFERENCE_DIV = '13' AND LM.LICENSE_TYPE NOT IN ('NA', 'PF')) AS IDENTIFICATION_SUB_STATUS_BOM , T1.DISTRIBUTE_TARGET , T1.COMPLETE_YN , T1.STATUS_REQUEST_YN , T5.NOTICE_TYPE , T5.NOTICE_TYPE_ETC , T1.PUBLIC_YN , T1.VULNERABILITY_RESOLUTION , T1.CVSS_SCORE_MAX FROM PROJECT_MASTER T1 LEFT JOIN T2_FILE T3 ON T3.FILE_ID = T1.SRC_ANDROID_NOTICE_FILE_ID LEFT JOIN T2_FILE T4 ON T4.FILE_ID = T1.SRC_ANDROID_RESULT_FILE_ID LEFT JOIN OSS_NOTICE T5 ON T1.PRJ_ID = T5.PRJ_ID
WHERE T1.USE_YN = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_PARTNER = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_DEP = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_SRC= 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_BAT = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_BIN = 'Y'
AND T1.IDENTIFICATION_SUB_STATUS_ANDROID = 'Y'
AND T1.IDENTIFICATION_STATUS = #{identificationStatus}
AND ( T1.CREATOR = #{loginUserName}
OR T1.REVIEWER = #{loginUserName}
OR EXISTS ( SELECT 1 FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} ) )
AND T1.PRJ_ID = #{prjId}
AND T1.PRJ_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{prjName}, '_', '\\\\_'),'%')
AND (T1.PRJ_ID = #{prjIdName} OR T1.PRJ_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{prjIdName}, '_', '\\\\_'),'%'))
AND T1.PRJ_VERSION LIKE CONCAT(REGEXP_REPLACE(#{prjVersion}, '_', '\\\\_'),'%')
AND T1.DISTRIBUTION_TYPE = #{distributionType}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= #{schEndDate}
AND T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%'))
AND T5.NOTICE_TYPE = #{noticeType}
AND T1.REVIEWER IS NULL
AND T1.REVIEWER IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{reviewer},'%'))
AND T1.PRJ_ID IN ( SELECT A.PRJ_ID AS WATCHER FROM PROJECT_WATCHER A WHERE T1.PRJ_ID = A.PRJ_ID AND (A.USER_ID LIKE CONCAT('%',#{watcher},'%') OR A.EMAIL LIKE CONCAT('%',#{watcher},'%')))
AND EXISTS (SELECT 1 FROM ( SELECT DISTINCT X.REFERENCE_ID FROM OSS_COMPONENTS X
LEFT OUTER JOIN OSS_COMPONENTS_LICENSE Y ON X.COMPONENT_ID = Y.COMPONENT_ID LEFT OUTER JOIN LICENSE_MASTER Z ON Y.LICENSE_ID = Z.LICENSE_ID
WHERE X.EXCLUDE_YN <> 'Y'
AND Y.EXCLUDE_YN <> 'Y'
AND X.REFERENCE_DIV != '20'
AND X.OSS_NAME = #{ossName}
AND X.OSS_VERSION = #{ossVersion}
AND (Y.LICENSE_NAME = #{licenseName} OR Z.LICENSE_NAME = #{licenseName} OR Z.SHORT_IDENTIFIER = #{licenseName})
AND X.REF_PARTNER_ID IN (SELECT P.PARTNER_ID FROM PARTNER_MASTER P WHERE P.USE_YN = 'Y' AND P.STATUS = 'CONF' AND P.PARTNER_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{refPartnerName}, '_', '\\\\_'),'%'))
) TBL WHERE TBL.REFERENCE_ID = T1.PRJ_ID )
AND EXISTS(SELECT 1 FROM BINARY_MASTER X LEFT OUTER JOIN T2_FILE Y ON Y.FILE_SEQ = X.BINARY_FILE_ID WHERE X.BAT_STATUS = '60' AND T1.PRJ_ID = X.PRJ_ID AND Y.ORIG_NM LIKE CONCAT('%',REGEXP_REPLACE(#{binaryName}, '_', '\\\\_'),'%'))
AND EXISTS ( SELECT 1 FROM OSS_COMPONENTS SUB_B1 INNER JOIN PROJECT_MASTER SUB_B2 ON SUB_B2.PRJ_ID = SUB_B1.REFERENCE_ID AND SUB_B2.USE_YN = 'Y' WHERE T1.PRJ_ID = SUB_B2.PRJ_ID AND SUB_B1.EXCLUDE_YN != 'Y' AND SUB_B1.REFERENCE_DIV IN ('10','11','12','14','15') AND SUB_B1.BINARY_NAME LIKE CONCAT('%', #{schBinaryName},'%') )
AND EXISTS (SELECT 1 FROM PROJECT_MODEL M WHERE T1.PRJ_ID = M.PRJ_ID AND MODEL_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{modelName}, '_', '\\\\_'),'%'))
AND T1.COMMENT LIKE CONCAT('%', #{comment}, '%')
AND EXISTS( SELECT 1 FROM COMMENTS_HISTORY COMM WHERE COMM.USE_YN = 'Y' AND COMM.REFERENCE_ID = T1.PRJ_ID AND COMM.REFERENCE_DIV IN ('19', '10', '12', '14') AND REGEXP_REPLACE(COMM.CONTENTS, '<img[^>]+>', '') LIKE CONCAT('%',#{userComment},'%') )
AND T1.NETWORK_SERVER_TYPE = #{networkServerType}
AND T1.SECMAIL_YN = #{secMailYn}
) A WHERE 1=1
AND EXISTS (SELECT 1 FROM T2_CODE_DTL S WHERE S.CD_NO ='200' AND S.CD_DTL_NM LIKE CONCAT('%', #{prjDivision}, '%') AND A.DIVISION = S.CD_DTL_NO)
AND
A.STATUS = #{item}
AND A.PRIORITY = #{priority}

mapper.update:
UPDATE PROJECT_MASTER 
   SET REVIEWER = #{reviewer}
      ,MODIFIER = #{modifier}
      ,MODIFIED_DATE = now()
 WHERE PRJ_ID = #{prjId}

UPDATE PROJECT_MASTER SET IDENTIFICATION_STATUS = NULL
,VERIFICATION_STATUS = NULL
,MODIFIER = #{modifier}, MODIFIED_DATE = now() WHERE PRJ_ID = #{prjId}

mapper.select:
/* ProjectMapper.selectProjectMaster */ SELECT A.PRJ_ID , A.PRJ_NAME , A.PRJ_VERSION , A.DISTRIBUTION_TYPE , A.NETWORK_SERVER_TYPE , CASE WHEN B.CONTENTS IS NULL THEN A.COMMENT ELSE B.CONTENTS END AS COMMENT , B.COMM_ID AS COMMENT_IDX , A.OS_TYPE , A.OS_TYPE_ETC , A.DIVISION , A.USE_CUSTOM_NOTICE_YN , A.IDENTIFICATION_STATUS , A.VERIFICATION_STATUS , A.DESTRIBUTION_STATUS , A.IDENTIFICATION_SUB_STATUS_PARTNER , A.IDENTIFICATION_SUB_STATUS_DEP , A.IDENTIFICATION_SUB_STATUS_SRC , A.IDENTIFICATION_SUB_STATUS_BAT , A.IDENTIFICATION_SUB_STATUS_BIN , A.IDENTIFICATION_SUB_STATUS_ANDROID , A.COMPLETE_YN , A.DROP_YN , A.REVIEWER , A.USE_YN , A.SECMAIL_YN , A.SECMAIL_DESC , A.COPY_PRJ_ID , A.WITHOUT_VERIFY_YN
/* 다운로드 허용 플래그 */
, IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x01, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_HTML_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x02, 'Y', 'N') AS ALLOW_DOWNLOAD_NOTICE_TEXT_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x04, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_HTML_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x08, 'Y', 'N') AS ALLOW_DOWNLOAD_SIMPLE_TEXT_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x10, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_SHEET_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x20, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_RDF_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x40, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_TAG_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x80, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_JSON_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x100, 'Y', 'N') AS ALLOW_DOWNLOAD_SPDX_YAML_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x200, 'Y', 'N') AS ALLOW_DOWNLOAD_CDX_JSON_YN , IF(A.ALLOW_DOWNLOAD_BIT_FLAG & 0x400, 'Y', 'N') AS ALLOW_DOWNLOAD_CDX_XML_YN
/** DISTRIBUTION **/ , A.DISTRIBUTE_MASTER_CATEGORY , A.DISTRIBUTE_NAME , A.DISTRIBUTE_SOFTWARE_TYPE , A.DISTRIBUTE_TARGET , A.DISTRIBUTE_DEPLOY_YN , A.DISTRIBUTE_DEPLOY_TIME , A.DISTRIBUTE_OSD_KEY
/** FILE_ID **/ , A.DEP_CSV_FILE_ID , A.SRC_CSV_FILE_ID , A.SRC_ANDROID_CSV_FILE_ID , A.SRC_ANDROID_NOTICE_FILE_ID , A.SRC_ANDROID_NOTICE_XML_ID , A.SRC_ANDROID_RESULT_FILE_ID , A.SCRT_CSV_FILE_ID , A.PACKAGE_FILE_ID , A.PACKAGE_FILE_ID2 , A.PACKAGE_FILE_ID3 , A.PACKAGE_FILE_ID4 , A.PACKAGE_FILE_TYPE1 , A.PACKAGE_FILE_TYPE2 , A.PACKAGE_FILE_TYPE3 , A.PACKAGE_FILE_TYPE4 , A.PACKAGE_VUL_DOC_FILE_ID , A.NOTICE_FILE_ID , A.REVIEW_REPORT_FILE_ID , A.ZIP_FILE_ID , A.BIN_CSV_FILE_ID , A.BIN_BINARY_FILE_ID
, A.README_YN , A.README_CONTENT , A.README_FILE_NAME , A.VERIFY_FILE_CONTENT , A.EXCEPT_FILE_CONTENT , A.BINARY_FILE_YN , A.CREATOR , A.CREATED_DATE , A.MODIFIER , A.MODIFIED_DATE , C.USER_NAME PRJ_USER_NAME , R.USER_NAME REVIEWER_NAME , (SELECT CD_DTL_NM FROM T2_CODE_DTL D WHERE D.CD_NO = '200' AND A.DIVISION = D.CD_DTL_NO) AS PRJ_DIVISION_NAME , A.PUBLIC_YN , A.COMM_ID , A.STATUS_REQUEST_YN , A.STATUS_VERIFY_YN , A.CHANGED_NOTICE_YN , CASE WHEN A.COMPLETE_YN = 'Y' AND A.STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN A.DROP_YN = 'Y' AND A.STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN A.IDENTIFICATION_STATUS = 'REV' OR A.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN A.IDENTIFICATION_STATUS = 'REQ' OR A.VERIFICATION_STATUS = 'REQ' OR A.STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'CONF' AND A.DESTRIBUTION_STATUS = 'DONE' THEN 'FREV' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'CONF' AND A.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' WHEN A.IDENTIFICATION_STATUS = 'CONF' AND A.VERIFICATION_STATUS = 'NA' AND A.DESTRIBUTION_STATUS = 'NA' THEN 'FREV' ELSE 'PROG' END STATUS , A.PRIORITY , A.PUBLIC_YN , A.VULNERABILITY_RESOLUTION , A.CVSS_SCORE_MAX , (SELECT COUNT(1) FROM OSS_COMPONENTS OC INNER JOIN OSS_COMPONENTS_LICENSE OCL ON OC.COMPONENT_ID = OCL.COMPONENT_ID AND OCL.EXCLUDE_YN <> 'Y' LEFT OUTER JOIN LICENSE_MASTER LM ON OCL.LICENSE_ID = LM.LICENSE_ID AND LM.USE_YN = 'Y' WHERE OC.REFERENCE_ID = A.PRJ_ID AND OC.REFERENCE_DIV = '13' AND LM.LICENSE_TYPE NOT IN ('NA', 'PF')) AS IDENTIFICATION_SUB_STATUS_BOM FROM PROJECT_MASTER A LEFT JOIN COMMENTS_HISTORY B ON A.COMMENT = B.COMM_ID LEFT OUTER JOIN T2_USERS C ON A.CREATOR = C.USER_ID
LEFT OUTER JOIN T2_USERS R ON A.REVIEWER = R.USER_ID WHERE A.PRJ_ID = #{prjId}

mapper.insert:
INSERT INTO	 PROJECT_MASTER
(

	PRJ_ID ,

	PRJ_NAME
	, PRJ_VERSION
	, DISTRIBUTION_TYPE
	, NETWORK_SERVER_TYPE
	, DISTRIBUTE_TARGET
	, COMMENT			
	, OS_TYPE			
	, OS_TYPE_ETC
	, CREATOR
	, MODIFIER

	, COPY_PRJ_ID

	, IDENTIFICATION_SUB_STATUS_PARTNER
	, IDENTIFICATION_SUB_STATUS_DEP
	, IDENTIFICATION_SUB_STATUS_SRC
	, IDENTIFICATION_SUB_STATUS_BAT
	, IDENTIFICATION_SUB_STATUS_BIN
	, IDENTIFICATION_SUB_STATUS_ANDROID
	, PUBLIC_YN
	, SECMAIL_YN
	, SECMAIL_DESC
	, PRIORITY
	, DIVISION
)
VALUES
(

	#{prjId} ,

	#{prjName}
	, #{prjVersion}
	, #{distributionType}
	, #{networkServerType}
	, #{distributeTarget}
	, #{comment}
	, #{osType}
	, #{osTypeEtc}
	, #{loginUserName}
	, #{loginUserName}

	, #{copyPrjId}

	, #{identificationSubStatusPartner}
	, #{identificationSubStatusDep}
	, #{identificationSubStatusSrc}
	, #{identificationSubStatusBat}
	, #{identificationSubStatusBin}
	, #{identificationSubStatusAndroid}
	, #{publicYn}
	, #{secMailYn}
	, #{secMailDesc}
	, #{priority}
	, (SELECT DIVISION FROM T2_USERS WHERE USER_ID = #{loginUserName})
) ON DUPLICATE KEY UPDATE

	PRJ_ID                            = #{prjId} ,

	PRJ_NAME                      = #{prjName}
	, PRJ_VERSION                 = #{prjVersion}
	, DISTRIBUTION_TYPE           = #{distributionType}
	, NETWORK_SERVER_TYPE		  = #{networkServerType}
	, DISTRIBUTE_TARGET			  = #{distributeTarget}
	, COMMENT                     = #{comment}
	, OS_TYPE                     = #{osType}
	, OS_TYPE_ETC                 = #{osTypeEtc}
	, PRIORITY					  = #{priority}
	, COMPLETE_YN                 = #{completeYn}
	, MODIFIER                    = #{loginUserName}
	, MODIFIED_DATE = now()
    , PUBLIC_YN                   = #{publicYn}
    , SECMAIL_YN                  = #{secMailYn}
    , SECMAIL_DESC                = #{secMailDesc}

	, CREATOR                    = #{creator}


	, DIVISION                    = #{division}

INSERT INTO PROJECT_MODEL
(			
	PRJ_ID,
	CATEGORY,
	SUBCATEGORY,
	MODEL_NAME,
	RELEASE_DATE

		,MODIFIER


		,MODIFIED_DATE


		, DEL_YN


		,OSDD_SYNC_YN, OSDD_SYNC_TIME

)
VALUES
(	
	#{prjId},


	substr(#{category},1,3),
	substr(#{category},4),


	substr(#{category},1,5),
	substr(#{category},6),


	#{modelName},
	#{releaseDate}

		,#{modifier}


