mapper.select:
/* getProjectNameList : PROJECT NAME AUTO COMPLETE */
	SELECT DISTINCT T1.PRJ_NAME
	FROM PROJECT_MASTER T1
	WHERE T1.USE_YN = 'Y'

		AND T1.IDENTIFICATION_STATUS = #{identificationStatus}


		AND (
			T1.CREATOR = #{creator} 
				OR EXISTS (
					SELECT A1.PRJ_ID
					FROM PROJECT_WATCHER A1

					WHERE A1.PRJ_ID = T1.PRJ_ID AND (A1.USER_ID = #{creator} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{creator}) ))
				OR T1.PUBLIC_YN = 'Y'
			)
		)

	ORDER BY T1.CREATED_DATE DESC

/* getProjectIdList : PROJECT ID AUTO COMPLETE */ SELECT DISTINCT T1.PRJ_ID FROM PROJECT_MASTER T1 WHERE T1.USE_YN = 'Y' AND T1.IDENTIFICATION_STATUS = 'CONF'
AND ( T1.CREATOR = #{creator} OR EXISTS ( SELECT A1.PRJ_ID FROM PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND (A1.USER_ID = #{creator} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{creator}) )) OR T1.PUBLIC_YN = 'Y' ) )
ORDER BY T1.CREATED_DATE DESC

/* getProjectVersionList : PROJECT VERSION AUTO COMPLETE */
	SELECT DISTINCT T1.PRJ_VERSION
	FROM PROJECT_MASTER T1
	WHERE T1.USE_YN = 'Y'

		AND (
			T1.CREATOR = #{creator} 
				OR EXISTS (
				SELECT A1.PRJ_ID 
				  FROM PROJECT_WATCHER A1 

				 WHERE A1.PRJ_ID = T1.PRJ_ID AND (A1.USER_ID = #{creator} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{creator}) ))
			)
		)

/* getProjectModelNameList : PROJECT MODEL AUTO COMPLETE */
	SELECT DISTINCT MODEL_NAME
	  FROM PROJECT_MODEL

SELECT COMPONENT_ID
  FROM OSS_COMPONENTS
 WHERE REFERENCE_ID = #{referenceId}
   AND REFERENCE_DIV = #{referenceDiv}

