UNION ALL
SELECT SUB1.OSS_ID, SUB2.OSS_NICKNAME AS OSS_NAME, SUB1.OSS_VERSION FROM ( SELECT OV.OSS_ID, OC.OSS_COMMON_ID, OC.OSS_NAME, OV.OSS_VERSION FROM OSS_COMMON OC LEFT OUTER JOIN OSS_VERSION OV ON OC.OSS_COMMON_ID = OV.OSS_COMMON_ID ) SUB1 INNER JOIN OSS_NICKNAME SUB2 ON SUB1.OSS_COMMON_ID = SUB2.OSS_COMMON_ID WHERE SUB1.OSS_ID = #{ossId}
) B ON A.OSS_NAME = B.OSS_NAME AND IFNULL(B.OSS_VERSION, '') = IFNULL(A.OSS_VERSION, '') WHERE T1.PRJ_ID = A.REFERENCE_ID AND A.REFERENCE_DIV IN ('10','11','12','14','15','16') AND A.EXCLUDE_YN = 'N' )
