mapper.select:
/* ProjectMapper identificationSubGrid */ SELECT RESULT.* , (SELECT X.OSS_LICENSE_COMB FROM OSS_LICENSE_DECLARED X WHERE X.OSS_ID = (SELECT M.OSS_ID FROM OSS_COMPONENTS M WHERE M.COMPONENT_ID = RESULT.COMPONENT_ID) AND X.LICENSE_ID = RESULT.LICENSE_ID AND X.OSS_LICENSE_IDX = RESULT.rnum) AS OSS_LICENSE_COMB FROM ( SELECT (CASE @vjob WHEN S.COMPONENT_ID THEN @rownum:=@rownum+1 ELSE @rownum:=1 END) rnum, (@vjob:=S.COMPONENT_ID) vjob, CONCAT(S.COMPONENT_ID, '-', S.COMPONENT_LICENSE_ID) AS GRID_ID, S.COMPONENT_LICENSE_ID, S.COMPONENT_ID, S.LICENSE_ID, IFNULL(S.LICENSE_NAME, '') AS LICENSE_NAME, IFNULL(S.LICENSE_TEXT, '') AS LICENSE_TEXT, IFNULL(S.COPYRIGHT_TEXT, '') AS COPYRIGHT_TEXT, S.EXCLUDE_YN FROM OSS_COMPONENTS_LICENSE S, (SELECT @vjob:='', @rownum:=0 FROM DUAL) b WHERE
#{_componentId}
S.COMPONENT_ID = #{componentId}
ORDER BY COMPONENT_ID, (CASE WHEN S.LICENSE_ID IS NULL THEN 99999 ELSE 1 END), COMPONENT_LICENSE_ID ASC ) RESULT ORDER BY COMPONENT_ID, RNUM ASC

