/* ProjectMapper.updateComponentsOssId */ UPDATE OSS_COMPONENTS A1 LEFT OUTER JOIN ( SELECT T1.OSS_ID, T1.OSS_VERSION , T1.OSS_NAME FROM ( SELECT OV.OSS_ID, OC.OSS_COMMON_ID, OC.OSS_NAME, OV.OSS_VERSION, OV.USE_YN FROM OSS_COMMON OC LEFT OUTER JOIN OSS_VERSION OV ON OC.OSS_COMMON_ID = OV.OSS_COMMON_ID ) T1 WHERE 1=1 AND EXISTS ( SELECT 1 FROM OSS_COMPONENTS WHERE REFERENCE_ID = #{prjId} AND OSS_NAME = T1.OSS_NAME AND OSS_VERSION = T1.OSS_VERSION ) AND T1.USE_YN = 'Y'
UNION
SELECT T1.OSS_ID, T1.OSS_VERSION, T2.OSS_NICKNAME AS OSS_NAME FROM OSS_VERSION T1 LEFT OUTER JOIN OSS_NICKNAME T2 ON T1.OSS_COMMON_ID = T2.OSS_COMMON_ID WHERE 1=1 AND EXISTS ( SELECT 1 FROM OSS_COMPONENTS WHERE REFERENCE_ID = #{prjId} AND OSS_NAME = T2.OSS_NICKNAME AND OSS_VERSION = T1.OSS_VERSION ) AND T1.USE_YN = 'Y' ) A2 ON A2.OSS_NAME = A1.OSS_NAME AND IFNULL(A2.OSS_VERSION, '') = IFNULL(A1.OSS_VERSION, '') SET A1.OSS_ID = A2.OSS_ID, A1.OSS_NAME = IFNULL(A2.OSS_NAME, A1.OSS_NAME) WHERE A1.REFERENCE_ID = #{prjId}
AND A1.REFERENCE_DIV = #{referenceDiv}
AND A1.REFERENCE_DIV = '13'
AND IFNULL(A1.OSS_NAME, '') <> '' AND A1.ADMIN_CHECK_YN = 'N'

