/* ProjectMapper.updateComponentsLicenseInfo */ UPDATE OSS_COMPONENTS_LICENSE A1 INNER JOIN OSS_COMPONENTS A2 ON A1.COMPONENT_ID = A2.COMPONENT_ID AND A2.REFERENCE_ID = #{prjId}
AND A2.REFERENCE_DIV = #{referenceDiv}
AND A2.REFERENCE_DIV = '13'
AND IFNULL(A2.OSS_ID, '') <> '' AND A2.ADMIN_CHECK_YN = 'N' INNER JOIN ( SELECT T1.OSS_ID, ( SELECT CASE WHEN IFNULL(T3.SHORT_IDENTIFIER, '') = '' THEN T3.LICENSE_NAME ELSE T3.SHORT_IDENTIFIER END FROM LICENSE_MASTER T3 WHERE T3.LICENSE_ID = T2.LICENSE_ID ) AS LICENSE_NAME , T2.LICENSE_ID , CASE WHEN IFNULL(T2.OSS_LICENSE_TEXT, '') = '' THEN (SELECT B1.LICENSE_TEXT FROM LICENSE_MASTER B1 WHERE B1.LICENSE_ID = T2.LICENSE_ID) ELSE T2.OSS_LICENSE_TEXT END AS OSS_LICENSE_TEXT , T2.OSS_COPYRIGHT FROM ( SELECT OV.OSS_ID FROM OSS_COMMON OC LEFT OUTER JOIN OSS_VERSION OV ON OC.OSS_COMMON_ID = OV.OSS_COMMON_ID WHERE EXISTS ( SELECT 1 FROM OSS_COMPONENTS OC WHERE OC.REFERENCE_ID = #{prjId}
AND OC.REFERENCE_DIV = #{referenceDiv}
AND OC.REFERENCE_DIV = '13'
AND OC.OSS_ID = OV.OSS_ID ) ) T1 INNER JOIN OSS_LICENSE_DECLARED T2 ON T1.OSS_ID = T2.OSS_ID GROUP BY T1.OSS_ID, T2.LICENSE_ID ) A3 ON (A3.OSS_ID = A2.OSS_ID AND A3.LICENSE_ID = A1.LICENSE_ID) SET A1.LICENSE_NAME = A3.LICENSE_NAME, A1.LICENSE_TEXT = A3.OSS_LICENSE_TEXT, A1.COPYRIGHT_TEXT = A3.OSS_COPYRIGHT WHERE IFNULL(A1.LICENSE_ID, '') <> '' AND A2.ADMIN_CHECK_YN = 'N'

