FROM T2_CODE_DTL A1 INNER JOIN T2_CODE A2 ON A1.CD_NO = A2.CD_NO WHERE A1.CD_NO IN ( SELECT T2.CD_DTL_EXP FROM T2_CODE T1 INNER JOIN T2_CODE_DTL T2 ON T1.CD_NO = T2.CD_NO AND T2.USE_YN = 'Y' WHERE T1.CD_NO = '500'
) AND A1.USE_YN = 'Y'
) RESULT ORDER BY Category, SubCategory

SELECT CONCAT(RESULT.Category, ' CHAR(62) ', RESULT.SubCategory) AS 'Desc' FROM ( SELECT ( SELECT SUB.CD_DTL_NM FROM T2_CODE_DTL SUB WHERE A1.CD_NO = SUB.CD_DTL_EXP AND SUB.CD_NO = '550') AS Category , A1.CD_DTL_NM AS SubCategory , ( SELECT SUB.CD_DTL_NO FROM T2_CODE_DTL SUB WHERE A1.CD_NO = SUB.CD_DTL_EXP AND SUB.CD_NO = '550') AS CategoryID , A1.CD_DTL_NO AS SubCategoryID
FROM T2_CODE_DTL A1 INNER JOIN T2_CODE A2 ON A1.CD_NO = A2.CD_NO WHERE A1.CD_NO IN ( SELECT T2.CD_DTL_EXP FROM T2_CODE T1 INNER JOIN T2_CODE_DTL T2 ON T1.CD_NO = T2.CD_NO AND T2.USE_YN = 'Y' WHERE T1.CD_NO = '550'
) AND A1.USE_YN = 'Y'
) RESULT ORDER BY Category, SubCategory

