/* ProjectMapper.selectModelList */ SELECT A2.MODEL_NAME , A.PRJ_ID , A.PRJ_NAME , A.STATUS , A.PRIORITY , A.DISTRIBUTION_TYPE , A.IDENTIFICATION_STATUS , A.VERIFICATION_STATUS , A.DESTRIBUTION_STATUS
, LEFT(REGEXP_REPLACE(A.CREATED_DATE, '[^0-9]', ''), 8) AS DISTRIBUTE_DEPLOY_TIME
, A.RELEASE_DATE AS DISTRIBUTE_DEPLOY_TIME
, A.DISTRIBUTE_TARGET , A.DIVISION , A.COMPLETE_YN FROM (SELECT T2.MODEL_NAME , T1.PRJ_ID , T1.PRJ_NAME , T1.COMPLETE_YN , CASE WHEN T1.PRJ_ID IS NULL THEN '' WHEN T1.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN T1.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN T1.IDENTIFICATION_STATUS = 'REV' OR T1.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'REQ' OR T1.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'CONF' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' WHEN T1.IDENTIFICATION_STATUS = 'CONF' AND T1.VERIFICATION_STATUS = 'NA' AND T1.DESTRIBUTION_STATUS = 'NA' THEN 'REV' ELSE 'PROG' END STATUS , T1.PRIORITY , T1.DISTRIBUTION_TYPE , IFNULL(T1.IDENTIFICATION_STATUS, '') AS IDENTIFICATION_STATUS , IFNULL(T1.VERIFICATION_STATUS, '') AS VERIFICATION_STATUS , IFNULL(T1.DESTRIBUTION_STATUS, '') AS DESTRIBUTION_STATUS , T2.RELEASE_DATE , T1.CREATED_DATE , T1.DISTRIBUTE_TARGET , T1.DIVISION FROM (SELECT * FROM PROJECT_MASTER WHERE 1 = 1 AND USE_YN = 'Y'
AND DATE_FORMAT(CREATED_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(CREATED_DATE,'%Y%m%d') <= #{schEndDate}
AND DIVISION = #{prjDivision}
) T1 LEFT JOIN (SELECT * FROM PROJECT_MODEL WHERE 1 = 1
AND DATE_FORMAT(RELEASE_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(RELEASE_DATE,'%Y%m%d') <= #{schEndDate}
) T2 ON T1.PRJ_ID = T2.PRJ_ID ) A INNER JOIN
SELECT #{_modelName} AS MODEL_NAME
ON A.MODEL_NAME = A2.MODEL_NAME
ON A.PRJ_NAME = A2.MODEL_NAME
ORDER BY MODEL_NAME ASC, PRJ_ID DESC
ORDER BY
MODEL_NAME
STATUS
PRJ_ID
(SELECT CD.CD_DTL_NM FROM T2_CODE_DTL CD WHERE CD.CD_NO = '207' AND CD.CD_DTL_NO = DISTRIBUTION_TYPE)
( CASE WHEN IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND IFNULL(DISTRIBUTE_TARGET, 'NA') != 'NA' AND DESTRIBUTION_STATUS = 'ERROR' THEN 7 WHEN IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND IFNULL(DISTRIBUTE_TARGET, 'NA') != 'NA' AND DESTRIBUTION_STATUS = 'DONE' THEN 6 WHEN IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND IFNULL(DISTRIBUTE_TARGET, 'NA') != 'NA' AND DESTRIBUTION_STATUS = 'RSV' THEN 5 WHEN IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND IFNULL(DISTRIBUTE_TARGET, 'NA') != 'NA' AND DESTRIBUTION_STATUS = 'PROG' THEN 4 WHEN (IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'NA' AND DESTRIBUTION_STATUS = 'NA') OR (IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND DISTRIBUTE_TARGET = 'NA' AND IFNULL(A.COMPLETE_YN, 'N') = 'Y') THEN 3 WHEN (IDENTIFICATION_STATUS = 'CONF' AND VERIFICATION_STATUS = 'CONF' AND DESTRIBUTION_STATUS = 'NA') OR (VERIFICATION_STATUS != '' AND VERIFICATION_STATUS != 'CONF' AND IFNULL(A.COMPLETE_YN, 'N') = 'Y') OR (DESTRIBUTION_STATUS = '' AND IFNULL(A.COMPLETE_YN, 'N') = 'Y') THEN 3 WHEN VERIFICATION_STATUS = 'CONF' AND IDENTIFICATION_STATUS = 'CONF' AND DESTRIBUTION_STATUS = '' AND IFNULL(A.COMPLETE_YN, 'N') != 'Y' THEN 2 ELSE 1 END )
DATE_FORMAT(DISTRIBUTE_DEPLOY_TIME, '%Y%m%d')
ASC
DESC

