AND	CAST(CVSS_SCORE AS DECIMAL(10, 1)) = CAST(#{cvssScore} AS DECIMAL(10,1))

mapper.insert:
        INSERT INTO
            NVD_DATA_SECURITY
            (
            REFERENCE_ID
            , OSS_NAME
            , OSS_VERSION

			, CVE_ID


			, CVSS_SCORE


			, PUBL_DATE


			, VULNERABILITY_RESOLUTION


			, SECURITY_PATCH_LINK

			, SECURITY_COMMENTS
            )
            VALUES
            (
            #{referenceId}
            , #{ossName}
            , REPLACE(#{ossVersion}, '-', '')

			, #{cveId}


			, #{cvssScore}


			, #{publDate}


			, #{vulnerabilityResolution}


			, #{securityPatchLink}


			, #{securityComments}


			, null

            ) ON DUPLICATE KEY UPDATE
           	VULNERABILITY_RESOLUTION	= #{vulnerabilityResolution}

			, SECURITY_COMMENTS = #{securityComments}


			, SECURITY_COMMENTS = null


			, SECURITY_PATCH_LINK = #{securityPatchLink}


			, PUBL_DATE	= #{publDate}

mapper.update:
UPDATE 
	NVD_DATA_SCORE_V3
SET 

	CVE_ID = null
	, CVSS_SCORE = null
	, VULN_SUMMARY = null


	CVE_ID = #{cveId}
	, CVSS_SCORE = #{cvssScore}
	, VULN_SUMMARY = #{vulnSummary}

WHERE 
	PRODUCT = #{ossName}
AND 
	VERSION = #{ossVersion}

UPDATE PROJECT_MASTER 
   SET CVSS_SCORE_MAX = #{cvssScoreMax}

		, VULNERABILITY_RESOLUTION = #{vulnerabilityResolution}


		, VULNERABILITY_RESOLUTION = NULL

 WHERE PRJ_ID = #{prjId}

mapper.delete:
/* ProjectMapper.resetOssComponentsAndLicense */
DELETE COMPONENT, LICENSE 
FROM OSS_COMPONENTS COMPONENT 
	LEFT JOIN OSS_COMPONENTS_LICENSE LICENSE ON COMPONENT.COMPONENT_ID = LICENSE.COMPONENT_ID
WHERE
 COMPONENT.REFERENCE_ID = #{referenceId} AND COMPONENT.REFERENCE_DIV = #{referenceDiv}

mapper.insert:
