	, REF_PARTNER_ID, REF_PRJ_ID, REF_BAT_ID, REF_COMPONENT_ID
	, REPORT_FILE_ID, MERGE_PRE_DIV, PRE_OBLIGATION_TYPE, OBLIGATION_TYPE
	, ADMIN_CHECK_YN, COMMENTS, REF_DIV, DEPENDENCIES, TLSH, CHECK_SUM, REF_OSS_NAME
	)
	VALUES

	(
	#{item.componentId}, #{item.referenceId}, #{item.referenceDiv}, ${item.componentIdx}
	, #{item.ossId}, #{item.ossName}, #{item.ossVersion}
	, #{item.downloadLocation}, #{item.homepage}, #{item.filePath}, #{item.packageUrl}, #{item.excludeYn}, #{item.copyright}
