/* SelfCheckMapper.selectProjectTotalCount */ SELECT COUNT(*) FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.DISTRIBUTION_TYPE , T1.COMMENT , T1.OS_TYPE , T1.OS_TYPE_ETC , IFNULL(T1.SRC_CSV_FILE_ID, '') SRC_CSV_FILE_ID , T1.DIVISION , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.MODIFIER = USER_ID), T1.MODIFIER) AS MODIFIER , T1.MODIFIED_DATE , IFNULL(T1.CVSS_SCORE, 0) AS CVSS_SCORE , T1.CVE_ID FROM PRE_PROJECT_MASTER T1 LEFT OUTER JOIN ( SELECT A1.REFERENCE_ID , MAX(A2.CVSS_SCORE) AS CVSS_SCORE , 'Y' AS VULN_YN FROM PRE_OSS_COMPONENTS A1 INNER JOIN OSS_MASTER A2 ON A1.OSS_NAME = A2.OSS_NAME AND A1.OSS_VERSION = A2.OSS_VERSION AND A2.VULN_YN = 'Y' WHERE A1.REFERENCE_DIV IN ('10','11','12','14','15') AND A1.EXCLUDE_YN = 'N' GROUP BY REFERENCE_ID ) T2 ON T1.PRJ_ID = T2.REFERENCE_ID WHERE T1.USE_YN = 'Y'
AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PRE_PROJECT_WATCHER A1 WHERE A1.PRJ_ID = T1.PRJ_ID AND A1.USER_ID = #{loginUserName} ) )
AND T1.PRJ_ID = #{prjId}
AND T1.PRJ_NAME LIKE CONCAT('%',#{prjName},'%')
AND T1.PRJ_VERSION LIKE CONCAT(REGEXP_REPLACE(#{prjVersion}, '_', '\\\\_'),'%')
AND T1.DISTRIBUTION_TYPE = #{distributionType}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= #{schEndDate}
AND T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%'))
AND EXISTS(SELECT 1 FROM PRE_OSS_COMPONENTS X LEFT OUTER JOIN PRE_OSS_COMPONENTS_LICENSE Y ON CONCAT(X.REFERENCE_ID, '-', X.REFERENCE_DIV, '-', X.COMPONENT_IDX) = Y.COMPONENT_ID WHERE T1.PRJ_ID = X.REFERENCE_ID
AND X.OSS_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{ossName}, '_', '\\\\_'),'%')
AND Y.LICENSE_NAME LIKE CONCAT('%',REGEXP_REPLACE(#{licenseName}, '_', '\\\\_'),'%')
)
) A WHERE 1=1
AND (A.DIVISION = #{prjDivision} )

/* SelfCheckMapper.selectProjectList */ SELECT * FROM ( SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.DISTRIBUTION_TYPE , T1.COMMENT , T1.OS_TYPE , T1.OS_TYPE_ETC , IFNULL(T1.SRC_CSV_FILE_ID, '') SRC_CSV_FILE_ID , T1.DIVISION , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.MODIFIER = USER_ID), T1.MODIFIER) AS MODIFIER , T1.MODIFIED_DATE , IFNULL(T1.CVSS_SCORE, 0) AS CVSS_SCORE , T1.CVE_ID , ( SELECT COUNT(1) FROM PRE_OSS_COMPONENTS SUB WHERE T1.PRJ_ID = SUB.REFERENCE_ID AND SUB.REFERENCE_DIV = '10' ) AS OSS_COUNT
