mapper.select:
SELECT RESULT.* , (SELECT X.OSS_LICENSE_COMB FROM OSS_LICENSE_DECLARED X WHERE X.OSS_ID = (SELECT M.OSS_ID FROM PRE_OSS_COMPONENTS M WHERE M.REFERENCE_DIV = #{referenceDiv} AND M.REFERENCE_ID = #{referenceId} AND M.COMPONENT_IDX = RESULT.COMPONENT_IDX) AND X.LICENSE_ID = RESULT.LICENSE_ID AND X.OSS_LICENSE_IDX = RESULT.rnum) AS OSS_LICENSE_COMB FROM ( SELECT (CASE @vjob WHEN S.COMPONENT_ID THEN @rownum:=@rownum+1 ELSE @rownum:=1 END) rnum, (@vjob:=S.COMPONENT_ID) vjob, CONCAT(S.COMPONENT_ID, '-', S.COMPONENT_LICENSE_IDX) AS GRID_ID, M.COMPONENT_IDX, S.COMPONENT_LICENSE_IDX, S.COMPONENT_ID, S.LICENSE_ID, S.LICENSE_NAME, S.LICENSE_TEXT, S.COPYRIGHT_TEXT, S.EXCLUDE_YN FROM PRE_OSS_COMPONENTS M, PRE_OSS_COMPONENTS_LICENSE S, (SELECT @vjob:='', @rownum:=0 FROM DUAL) b WHERE M.REFERENCE_DIV = #{referenceDiv} AND M.REFERENCE_ID = #{referenceId} AND CONCAT(M.REFERENCE_ID, '-', M.REFERENCE_DIV, '-', M.COMPONENT_IDX) = S.COMPONENT_ID
#{_componentId}
AND S.COMPONENT_ID = #{componentId}
ORDER BY COMPONENT_ID, (CASE WHEN S.LICENSE_ID IS NULL THEN 99999 ELSE 1 END), COMPONENT_LICENSE_IDX ASC ) RESULT ORDER BY COMPONENT_IDX, RNUM ASC

