		INNER JOIN OSS_MASTER A2 ON A1.OSS_NAME = A2.OSS_NAME AND A1.OSS_VERSION = A2.OSS_VERSION AND A2.USE_YN = 'Y'
	SET A1.DOWNLOAD_LOCATION = A2.DOWNLOAD_LOCATION, A1.HOMEPAGE = A2.HOMEPAGE
	WHERE 
		A1.REFERENCE_ID = #{prjId} 


				AND A1.REFERENCE_DIV = #{referenceDiv}


				AND A1.REFERENCE_DIV = '13'


		 AND IFNULL(A1.OSS_ID, '') <> ''

/* ProjectMapper.updateComponentsLicenseId */ UPDATE PRE_OSS_COMPONENTS_LICENSE A1 INNER JOIN PRE_OSS_COMPONENTS A2 ON CONCAT(A2.REFERENCE_ID, '-', A2.REFERENCE_DIV, '-', A2.COMPONENT_IDX) = A1.COMPONENT_ID AND A2.REFERENCE_ID = #{prjId}
AND A2.REFERENCE_DIV = #{referenceDiv}
AND A2.REFERENCE_DIV = '10'
LEFT OUTER JOIN ( SELECT T1.LICENSE_ID, T1.LICENSE_NAME FROM LICENSE_MASTER T1 WHERE T1.USE_YN = 'Y' AND IFNULL(T1.SHORT_IDENTIFIER, '') = '' UNION SELECT T1.LICENSE_ID, T1.SHORT_IDENTIFIER AS LICENSE_NAME FROM LICENSE_MASTER T1 WHERE T1.USE_YN = 'Y' AND IFNULL(T1.SHORT_IDENTIFIER, '') != '' UNION SELECT T1.LICENSE_ID, T2.LICENSE_NICKNAME AS LICENSE_NAME FROM LICENSE_MASTER T1 INNER JOIN LICENSE_NICKNAME T2 ON T1.LICENSE_NAME = T2.LICENSE_NAME WHERE T1.USE_YN = 'Y' ) A3 ON A3.LICENSE_NAME = A1.LICENSE_NAME
SET A1.LICENSE_ID = A3.LICENSE_ID WHERE IFNULL(A1.LICENSE_NAME, '') <> ''

