SELECT T1.PRJ_ID , T1.DISTRIBUTE_TARGET , T1.DISTRIBUTE_NAME , T1.DISTRIBUTE_MASTER_CATEGORY , T1.DISTRIBUTE_DEPLOY_TIME , T2.* , (SELECT B1.ORIG_NM FROM T2_FILE B1 WHERE B1.FILE_SEQ = T1.NOTICE_FILE_ID) AS NOTICE_FILE_NAME , (SELECT C1.ORIG_NM FROM T2_FILE C1 WHERE C1.FILE_SEQ = T1.PACKAGE_FILE_ID) AS PACKAGE_FILE_NAME , GROUP_CONCAT(T3.CATEGORY, '@',T3.SUBCATEGORY, '@',T3.MODEL_NAME, '@', T3.RELEASE_DATE ORDER BY T3.CATEGORY, T3.SUBCATEGORY, T3.MODEL_NAME SEPARATOR '|') AS MODEL_INFO , T4.NOTICE_TYPE FROM PROJECT_MASTER T1 LEFT OUTER JOIN ( SELECT A1.REFERENCE_ID , COUNT(IF(A1.REFERENCE_DIV = '10', 1, NULL)) AS PARTNER_CNT , COUNT(IF(A1.REFERENCE_DIV = '11', 1, NULL)) AS SRC_CNT , COUNT(IF(A1.REFERENCE_DIV = '12', 1, NULL)) AS BAT_CNT , COUNT(IF(A1.REFERENCE_DIV = '13' AND A1.OBLIGATION_TYPE IN ('10', '11'), 1, NULL)) AS BOM_CNT , COUNT(IF(A1.REFERENCE_DIV = '13' AND A1.OBLIGATION_TYPE = '11', 1, NULL)) AS DISCLOSE_CNT FROM OSS_COMPONENTS A1 WHERE A1.REFERENCE_DIV IN ('10', '11', '12', '13') GROUP BY REFERENCE_ID ) T2 ON T1.PRJ_ID = T2.REFERENCE_ID LEFT OUTER JOIN PROJECT_MODEL T3 ON T1.PRJ_ID = T3.PRJ_ID LEFT OUTER JOIN OSS_NOTICE T4 ON T1.PRJ_ID = T4.PRJ_ID WHERE
#{_prjId}
GROUP BY PRJ_ID

