mapper.select:
SELECT IFNULL(MAX(CVSS_SCORE), 0) AS CVSS_SCORE , CVE_ID
FROM NVD_DATA_SCORE_V3 NVD 
WHERE (
		NVD.PRODUCT = #{ossName}

			OR NVD.PRODUCT = #{ossNameTemp}

		OR
		EXISTS(
			SELECT 1
			FROM OSS_MASTER SUB1
				INNER JOIN OSS_NICKNAME SUB2 ON SUB1.OSS_NAME = SUB2.OSS_NAME
			WHERE SUB1.USE_YN='Y' AND SUB1.OSS_NAME = #{ossName} AND SUB2.OSS_NICKNAME = NVD.PRODUCT
		)
	) AND NVD.VERSION LIKE CONCAT(#{ossVersion},'%')

SELECT T1.DIVISION AS PRJ_DIVISION , T1.USER_ID AS PRJ_USER_ID , T2.USER_NAME AS PRJ_USER_NAME , T1.EMAIL AS PRJ_EMAIL , T2.USE_YN AS USER_USE_YN , T3.USE_YN AS DEPT_USE_YN
FROM PROJECT_WATCHER T1 INNER JOIN T2_USERS T2 ON T1.USER_ID = T2.USER_ID LEFT JOIN T2_CODE_DTL T3 ON T1.DIVISION = T3.CD_DTL_NO AND T3.CD_NO = '200' WHERE T1.PRJ_ID = #{listId}
AND T1.PRJ_ID IN (SELECT DISTINCT A.PRJ_ID FROM PROJECT_MASTER A WHERE A.USE_YN = 'Y' AND (A.CREATOR = #{loginUserName} OR EXISTS (SELECT 1 FROM PROJECT_WATCHER A1
WHERE A1.PRJ_ID = A.PRJ_ID AND (A1.USER_ID = #{loginUserName} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{loginUserName}) )) ) OR A.PUBLIC_YN = 'Y') )
FROM PARTNER_WATCHER T1 INNER JOIN T2_USERS T2 ON T1.USER_ID = T2.USER_ID LEFT JOIN T2_CODE_DTL T3 ON T1.DIVISION = T3.CD_DTL_NO AND T3.CD_NO = '200' WHERE T1.PARTNER_ID = #{listId}
AND T1.PARTNER_ID IN (SELECT DISTINCT A.PARTNER_ID FROM PARTNER_MASTER A WHERE A.USE_YN = 'Y' AND (A.CREATOR = #{loginUserName} OR EXISTS (SELECT 1 FROM PARTNER_WATCHER A1
WHERE A1.PARTNER_ID = A.PARTNER_ID AND (A1.USER_ID = #{loginUserName} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{loginUserName}) )) ) OR A.PUBLIC_YN = 'Y') )
FROM BAT_WATCHER T1 INNER JOIN T2_USERS T2 ON T1.USER_ID = T2.USER_ID LEFT JOIN T2_CODE_DTL T3 ON T1.DIVISION = T3.CD_DTL_NO AND T3.CD_NO = '200' WHERE T1.BAT_ID = #{listId}

/* SelfCheckMapper.getSelfCheckList */ SELECT * FROM (SELECT T1.PRJ_ID , T1.PRJ_NAME , T1.PRJ_VERSION , T1.OS_TYPE , T1.OS_TYPE_ETC , (SELECT CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '207' AND CD_DTL_NO = T1.DISTRIBUTION_TYPE) AS DISTRIBUTION_TYPE
, (SELECT CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND CD_DTL_NO = T1.DIVISION) AS DIVISION , T1.CVSS_SCORE , IFNULL((SELECT USER_NAME FROM T2_USERS WHERE T1.CREATOR = USER_ID), T1.CREATOR) AS CREATOR , T1.CREATED_DATE , T2.* FROM PRE_PROJECT_MASTER T1 LEFT OUTER JOIN (SELECT M.REFERENCE_ID , COUNT(IF(L1.OBLIGATION_TYPE IN ('10', '11'), 1, NULL)) AS BOM_CNT , COUNT(IF(L1.OBLIGATION_TYPE = '11', 1, NULL)) AS DISCLOSE_CNT FROM PRE_OSS_COMPONENTS M LEFT OUTER JOIN OSS_MASTER L1 ON M.OSS_NAME = L1.OSS_NAME AND M.OSS_VERSION = L1.OSS_VERSION AND L1.USE_YN = 'Y' GROUP BY M.REFERENCE_ID ) T2 ON T1.PRJ_ID = T2.REFERENCE_ID WHERE 1 = 1 AND PRJ_ID IN ( SELECT A.PRJ_ID FROM PRE_PROJECT_MASTER A WHERE A.USE_YN = 'Y'
AND ( T1.CREATOR = #{loginUserName} OR EXISTS ( SELECT 1 FROM PRE_PROJECT_WATCHER A1
WHERE A1.PRJ_ID = T1.PRJ_ID AND (A1.USER_ID = #{loginUserName} OR (A1.USER_ID = 'all' AND A1.DIVISION = (SELECT DIVISION FROM T2_USERS A2 WHERE A2.USER_ID = #{loginUserName}) )) ) )
)
AND T1.PRJ_ID = #{prjId}
AND T1.PRJ_NAME LIKE CONCAT('%',#{prjName},'%')
AND T1.PRJ_VERSION LIKE CONCAT(#{prjVersion},'%')
AND T1.DISTRIBUTION_TYPE = #{distributionType}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') >= #{schStartDate}
AND DATE_FORMAT(T1.CREATED_DATE,'%Y%m%d') <= #{schEndDate}
AND T1.CREATOR IN (SELECT X.USER_ID FROM T2_USERS X WHERE X.USER_NAME LIKE CONCAT('%',#{creator},'%'))
AND EXISTS(SELECT 1 FROM PRE_OSS_COMPONENTS X LEFT OUTER JOIN PRE_OSS_COMPONENTS_LICENSE Y ON CONCAT(X.REFERENCE_ID, '-', X.REFERENCE_DIV, '-', X.COMPONENT_IDX) = Y.COMPONENT_ID WHERE T1.PRJ_ID = X.REFERENCE_ID
AND X.OSS_NAME LIKE CONCAT('%',#{ossName},'%')
AND Y.LICENSE_NAME LIKE CONCAT('%',#{licenseName},'%')
)
GROUP BY PRJ_ID ) A WHERE 1=1
AND ( A.DIVISION = #{prjDivision} )
ORDER BY PRJ_ID DESC

