mapper.select:
SELECT RTN.CD_DTL_NM AS DIVISION_NM , RTN.CD_ORDER AS DIVISION_ORDER
, MAX(RTN.CATEGORY${index}) AS CATEGORY${index}_CNT
, MAX(RTN.CATEGORY${titleArray.size}) AS CATEGORY${titleArray.size}_CNT
FROM (SELECT A.CD_DTL_NM , A.CD_ORDER
, IF(STAT.CD_DTL_NM IS NOT NULL AND STAT.CATEGORY IS NULL, COUNT(1), 0) AS CATEGORY${index}
, IF(STAT.CATEGORY = #{item}, COUNT(1), 0) AS CATEGORY${index}
, IF(
STAT.CATEGORY = #{user}
, COUNT(1), 0) AS CATEGORY${titleArray.size}
FROM (SELECT CD_DTL_NM , CD_ORDER FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') A
LEFT JOIN (SELECT T2.CD_DTL_NM , CASE WHEN PM.COMPLETE_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'COMP' WHEN PM.DROP_YN = 'Y' AND STATUS_REQUEST_YN = 'N' THEN 'DROP' WHEN PM.IDENTIFICATION_STATUS = 'REV' OR PM.VERIFICATION_STATUS = 'REV' THEN 'REV' WHEN PM.IDENTIFICATION_STATUS = 'REQ' OR PM.VERIFICATION_STATUS = 'REQ' OR STATUS_REQUEST_YN = 'Y' THEN 'REQ' WHEN PM.IDENTIFICATION_STATUS = 'CONF' AND PM.VERIFICATION_STATUS = 'CONF' AND PM.DESTRIBUTION_STATUS = 'DONE' THEN 'REV' WHEN PM.IDENTIFICATION_STATUS = 'CONF' AND PM.VERIFICATION_STATUS = 'CONF' AND PM.DESTRIBUTION_STATUS = 'NA' THEN 'REV' WHEN PM.IDENTIFICATION_STATUS = 'CONF' AND PM.VERIFICATION_STATUS = 'NA' AND PM.DESTRIBUTION_STATUS = 'NA' THEN 'REV' ELSE 'PROG' END CATEGORY FROM PROJECT_MASTER PM INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND PM.USE_YN ='Y') STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
LEFT JOIN (SELECT T2.CD_DTL_NM , ADMIN.USER_NAME AS CATEGORY FROM PROJECT_MASTER PM LEFT JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID LEFT JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO LEFT JOIN (SELECT 'unassigned' AS USER_ID, null AS USER_NAME UNION ALL SELECT USR.USER_ID , USR.USER_NAME FROM T2_USERS USR INNER JOIN T2_AUTHORITIES AUTH ON USR.USER_ID = AUTH.USER_ID AND AUTH.AUTHORITY='ROLE_ADMIN') ADMIN ON PM.REVIEWER = ADMIN.USER_ID WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND PM.USE_YN ='Y') STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
LEFT JOIN (SELECT T2.CD_DTL_NM , (SELECT SUBSTRING_INDEX(CD_DTL_NM, '(', 1) AS CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '207' AND CD_DTL_NO = PM.DISTRIBUTION_TYPE) AS CATEGORY FROM PROJECT_MASTER PM INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND PM.USE_YN ='Y' ) STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
LEFT JOIN (SELECT T2.CD_DTL_NM , CASE WHEN NETWORK_SERVER_TYPE = 'Y' THEN 'Network Service' ELSE 'Others' END CATEGORY FROM PROJECT_MASTER PM INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND PM.USE_YN ='Y' ) STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
) RTN GROUP BY RTN.CD_DTL_NM ORDER BY RTN.CD_ORDER ASC

SELECT OSS.COLUMN_NAME , COUNT(1) AS COLUMN_CNT FROM (
SELECT OC.OSS_NAME AS COLUMN_NAME FROM (SELECT T1.REFERENCE_ID, T1.OSS_NAME FROM OSS_COMPONENTS T1 INNER JOIN PROJECT_MASTER T2 ON T1.REFERENCE_ID = T2.PRJ_ID WHERE T1.REFERENCE_DIV IN ('13', '14') AND T2.IDENTIFICATION_STATUS = 'CONF' AND T1.OSS_NAME NOT IN ('', '-') AND T2.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND T2.USE_YN = 'Y' AND T1.EXCLUDE_YN = 'N') OC INNER JOIN PROJECT_MASTER PM ON OC.REFERENCE_ID = PM.PRJ_ID INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE T2.CD_DTL_NO = #{divisionNo} GROUP BY OC.REFERENCE_ID, OC.OSS_NAME
SELECT OC.OSS_NAME AS COLUMN_NAME FROM OSS_COMPONENTS OC INNER JOIN PROJECT_MASTER PM ON OC.REFERENCE_ID = PM.PRJ_ID WHERE OC.REFERENCE_DIV IN ('13', '14') AND PM.IDENTIFICATION_STATUS = 'CONF' AND OC.OSS_NAME NOT IN ('', '-') AND PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND PM.USE_YN = 'Y' AND OC.EXCLUDE_YN = 'N' GROUP BY OC.REFERENCE_ID, OC.OSS_NAME
) OSS GROUP BY OSS.COLUMN_NAME ORDER BY COLUMN_CNT DESC LIMIT 0, #{pieSize}

SELECT LICENSE.COLUMN_NAME , COUNT(1) AS COLUMN_CNT FROM (
SELECT LICENSE_NAME AS COLUMN_NAME FROM (SELECT T1.COMPONENT_ID, T1.REFERENCE_ID FROM OSS_COMPONENTS T1 INNER JOIN PROJECT_MASTER T2 ON T1.REFERENCE_ID = T2.PRJ_ID WHERE T1.REFERENCE_DIV IN ('13', '14') AND T2.IDENTIFICATION_STATUS = 'CONF' AND T1.OSS_NAME NOT IN ('', '-') AND T1.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND T2.USE_YN = 'Y' AND T1.EXCLUDE_YN = 'N') OC INNER JOIN OSS_COMPONENTS_LICENSE CL ON OC.COMPONENT_ID = CL.COMPONENT_ID AND UPPER(CL.LICENSE_NAME) NOT IN ('' , 'LGE PROPRIETARY LICENSE' , 'OTHER PROPRIETARY LICENSE') INNER JOIN PROJECT_MASTER PM ON OC.REFERENCE_ID = PM.PRJ_ID INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE T2.CD_DTL_NO = #{divisionNo} GROUP BY REFERENCE_ID, LICENSE_NAME
SELECT LICENSE_NAME AS COLUMN_NAME FROM (SELECT T1.COMPONENT_ID, T1.REFERENCE_ID FROM OSS_COMPONENTS T1 INNER JOIN PROJECT_MASTER T2 ON T1.REFERENCE_ID = T2.PRJ_ID WHERE T1.REFERENCE_DIV IN ('13', '14') AND T2.IDENTIFICATION_STATUS = 'CONF' AND T1.OSS_NAME NOT IN ('', '-') AND T2.CREATED_DATE BETWEEN #{startDate} AND #{endDate} AND T2.USE_YN = 'Y' AND T1.EXCLUDE_YN = 'N') OC INNER JOIN OSS_COMPONENTS_LICENSE CL ON OC.COMPONENT_ID = CL.COMPONENT_ID AND UPPER(CL.LICENSE_NAME) NOT IN ('' , 'LGE PROPRIETARY LICENSE' , 'OTHER PROPRIETARY LICENSE') GROUP BY OC.REFERENCE_ID, CL.LICENSE_NAME
) LICENSE GROUP BY LICENSE.COLUMN_NAME ORDER BY COLUMN_CNT DESC LIMIT 0, #{pieSize}

SELECT RTN.CD_DTL_NM AS DIVISION_NM
, MAX(RTN.CATEGORY${index}) AS CATEGORY${index}_CNT
, MAX(RTN.CATEGORY${titleArray.size}) AS CATEGORY${titleArray.size}_CNT
FROM (SELECT A.CD_DTL_NM , A.CD_ORDER
, IF(STAT.CD_DTL_NM IS NOT NULL AND STAT.CATEGORY IS NULL, COUNT(1), 0) AS CATEGORY${index}
, IF(STAT.CATEGORY = #{item}, COUNT(1), 0) AS CATEGORY${index}
, IF(
STAT.CATEGORY = #{user}
, COUNT(1), 0) AS CATEGORY${titleArray.size}
FROM (SELECT CD_DTL_NM , CD_ORDER FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') A
LEFT JOIN (SELECT T2.CD_DTL_NM , PM.STATUS AS CATEGORY FROM PARTNER_MASTER PM INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID AND PM.USE_YN = 'Y' INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate}) STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
LEFT JOIN (SELECT T2.CD_DTL_NM , ADMIN.USER_NAME AS CATEGORY FROM PARTNER_MASTER PM INNER JOIN T2_USERS T1 ON PM.CREATOR = T1.USER_ID AND PM.USE_YN = 'Y' INNER JOIN (SELECT CD_DTL_NO , CD_DTL_NM FROM T2_CODE_DTL WHERE CD_NO = '200' AND USE_YN = 'Y') T2 ON T1.DIVISION = T2.CD_DTL_NO LEFT JOIN (SELECT 'unassigned' AS USER_ID, null AS USER_NAME UNION ALL SELECT USR.USER_ID , USR.USER_NAME FROM T2_USERS USR INNER JOIN T2_AUTHORITIES AUTH ON USR.USER_ID = AUTH.USER_ID AND AUTH.AUTHORITY='ROLE_ADMIN') ADMIN ON PM.REVIEWER = ADMIN.USER_ID WHERE PM.CREATED_DATE BETWEEN #{startDate} AND #{endDate}) STAT ON A.CD_DTL_NM = STAT.CD_DTL_NM GROUP BY A.CD_DTL_NM, STAT.CATEGORY
) RTN GROUP BY RTN.CD_DTL_NM ORDER BY RTN.CD_ORDER ASC

SELECT COMPONENT_NAME AS COLUMN_NAME
     , COUNT(1) AS COLUMN_CNT
  FROM STATISTICS_MOSTUSED
 WHERE CHART_TYPE = #{chartType}
   AND DATE_FORMAT(CREATED_DATE, '%Y%m%d') BETWEEN ${startDate} AND ${endDate}
 
   AND DIV_NO = #{divisionNo}
 
 GROUP BY COMPONENT_NAME
 ORDER BY COLUMN_CNT DESC
 LIMIT 0, #{pieSize}
