        , #{creator}
        , #{modifier}
    )

mapper.update:
UPDATE 
	T2_USERS 
SET USER_ID = #{userId}

    , USER_NAME = #{userName}


    , DIVISION = #{division}


    , CREATED_DATE = #{createdDate}


    , PASSWORD = #{password}


	, EMAIL = #{email}


	, DIVISION = #{division}


    , MODIFIER = #{modifier}


    , MODIFIED_DATE = now()


    , EXPIRE_DATE = now()

	, USE_YN = #{useYn}
WHERE 
	USER_ID = #{userId}

mapper.select:
/* T2UserMapper.selectReviwer */ SELECT T1.USER_ID , T1.USER_NAME , T1.EMAIL , T1.DIVISION FROM T2_USERS T1 INNER JOIN T2_AUTHORITIES T2 ON T1.USER_ID = T2.USER_ID AND T2.AUTHORITY = 'ROLE_ADMIN' WHERE T1.USE_YN = #{adminYn}
AND T1.PASSWORD IS NULL
ORDER BY USER_NAME

