mapper.select:
/* VerificationMapper.selectVerifyOssList */ SELECT T2.COMPONENT_ID ,T2.COMPONENT_IDX /* 2018-08-06 choye 추가 (ETC) */ ,T2.OSS_ID ,T2.OSS_NAME ,T2.OSS_VERSION ,T2.DOWNLOAD_LOCATION ,T2.HOMEPAGE ,T2.REF_COMPONENT_ID ,T2.REFERENCE_ID ,CASE WHEN T2.REFERENCE_DIV = '10' THEN '3rd' WHEN T2.REFERENCE_DIV = '11' THEN 'SRC' WHEN T2.REFERENCE_DIV = '12' THEN 'BAT' WHEN T2.REFERENCE_DIV = '15' THEN 'BIN' WHEN T2.REFERENCE_DIV = '14' THEN 'BIN(Android)' END AS REFERENCE_DIV , GROUP_CONCAT(T3.LICENSE_NAME ORDER BY T3.LICENSE_ID DESC SEPARATOR ',') AS LICENSE_NAME , T2.FILE_PATH , T2.VERIFY_FILE_COUNT FROM ( SELECT A1.COMPONENT_ID ,A1.COMPONENT_IDX /* 2018-08-06 choye 추가 (ETC) */ ,A1.OSS_ID ,A1.OSS_NAME ,A1.OSS_VERSION ,A1.DOWNLOAD_LOCATION ,A1.REFERENCE_ID ,A1.HOMEPAGE ,A1.FILE_PATH ,A1.VERIFY_FILE_COUNT ,A1.REF_COMPONENT_ID ,(SELECT REFERENCE_DIV FROM OSS_COMPONENTS A2 WHERE A2.COMPONENT_ID = A1.REF_COMPONENT_ID) AS REFERENCE_DIV FROM OSS_COMPONENTS A1 WHERE A1.REFERENCE_ID = #{prjId} AND A1.REFERENCE_DIV='50' AND A1.MERGE_PRE_DIV IS NULL
AND A1.OBLIGATION_TYPE = '11'
) T2 INNER JOIN OSS_COMPONENTS_LICENSE T3 ON T2.COMPONENT_ID = T3.COMPONENT_ID WHERE 1=1
AND EXISTS ( SELECT 1 FROM LICENSE_MASTER TSUB1 WHERE TSUB1.USE_YN = 'Y' AND ( TSUB1.SHORT_IDENTIFIER REGEXP (SELECT GROUP_CONCAT(TRIM(CD_DTL_NM) SEPARATOR '|') FROM T2_CODE_DTL WHERE CD_NO = '122' AND USE_YN = 'Y') OR TSUB1.LICENSE_NAME REGEXP (SELECT GROUP_CONCAT(TRIM(CD_DTL_NM) SEPARATOR '|') FROM T2_CODE_DTL WHERE CD_NO = '122' AND USE_YN = 'Y') ) AND T3.LICENSE_ID = TSUB1.LICENSE_ID )
GROUP BY COMPONENT_ID ORDER BY COMPONENT_ID

